mapper.select:
SELECT A.GROUP_SEQ, TO_CHAR(C.PAY_ITM_REF_DT,'DD/MM/YYYY') AS PAY_ITM_REF_DT, A.PAY_ITM_MODE_ID, E.CODE AS PAY_ITM_MODE_NM, NVL(SUM (A.PAY_ITM_AMT),0) AS TOT_AMT, NVL(MAX (C.PAY_ITM_BANK_CHRG_AMT),0) AS BANK_CHG_AMT, CASE WHEN A.PAY_ITM_MODE_ID = 106 THEN MAX(C.PAY_ITM_CHQ_NO) ELSE MAX (C.PAY_ITM_BANK_IN_SLIP_NO) END AS PAY_ITM_BANK_IN_SLIP_NO, MAX (B.REF_DTL) AS REF_DTL, MAX (C.PAY_ITM_BANK_ACC_ID) AS PAY_ITM_BANK_ACC_ID, MAX (D.ACC_DESC) AS PAY_ITM_BANK_ACC_NM FROM PAY0252T A JOIN PAY0064D B ON A.PAY_ID = B.PAY_ID JOIN PAY0065D C ON A.PAY_ITM_ID = C.PAY_ITM_ID LEFT JOIN SYS0001M D ON C.PAY_ITM_BANK_ACC_ID = D.ACC_ID LEFT JOIN SYS0013M E ON A.PAY_ITM_MODE_ID = E.CODE_ID WHERE A.BANK_STATE_MAPPING_STUS_ID = 1 AND A.BANK_CHG_MAPPING_STUS_ID = 1 AND (A.REV_STUS_ID IS NULL OR A.REV_STUS_ID = 6) AND A.GROUP_SEQ IN ( SELECT DISTINCT A.GROUP_SEQ FROM PAY0252T A JOIN PAY0064D B ON A.PAY_ID = B.PAY_ID JOIN PAY0065D C ON A.PAY_ITM_ID = C.PAY_ITM_ID /*JOIN SAL0001D D ON B.SALES_ORD_ID = D.SALES_ORD_ID*/ WHERE C.PAY_ITM_REF_DT >= TO_DATE(#{transDateFr},'DD/MM/YYYY') AND C.PAY_ITM_REF_DT < TO_DATE(#{transDateTo},'DD/MM/YYYY') + 1
AND C.PAY_ITM_BANK_ACC_ID = #{bankAcc}
AND C.PAY_ITM_MODE_ID = #{payType}
AND B.VA_ACC_NO = #{vaAccount}
AND B.BRNCH_ID = #{branchId}
AND A.KEY_IN_SCRN = 'ADV' ) GROUP BY A.GROUP_SEQ, TO_CHAR(C.PAY_ITM_REF_DT,'DD/MM/YYYY'), A.PAY_ITM_MODE_ID, E.CODE

SELECT B.F_TRNSC_ID AS F_TRNSC_ID, A.BANK_ID AS BANK_ID, C.NAME AS BANK_NAME, A.F_BANK_JRNL_ACC_ID AS BANK_ACC, D.ACC_CODE || ' - ' || D.ACC_DESC AS BANK_ACC_NAME, TO_CHAR(B.F_TRNSC_DT, 'DD/MM/YYYY')F_TRNSC_DT, B.F_TRNSC_REF_CHQ_NO, B.F_TRNSC_REF_VA_NO, B.F_TRNSC_REF3, B.F_TRNSC_REF1, B.F_TRNSC_REF4, B.F_TRNSC_REF2, B.F_TRNSC_REF6, B.F_TRNSC_REM, B.F_TRNSC_DEBT_AMT, B.F_TRNSC_CRDIT_AMT, B.F_TRNSC_TELLER_ID, B.F_TRNSC_NEW_CHQ_NO, (SELECT COUNT(1) FROM PAY0081D WHERE F_BANK_JRNL_ID = A.F_BANK_JRNL_ID AND CRC_MAPPING_STUS_ID =1 AND OTH_KEYIN_STUS_ID =1 AND OTH_KEYIN_BC_STUS_ID = 1) AS COUNT FROM PAY0079D A JOIN PAY0081D B ON A.F_BANK_JRNL_ID = B.F_BANK_JRNL_ID LEFT JOIN SYS0004M C ON A.BANK_ID = C.BANK_ID LEFT JOIN SYS0001M D ON A.F_BANK_JRNL_ACC_ID = D.ACC_ID AND D.IS_PAY_CASH = 1 WHERE A.TRNSC_DT >= TO_DATE(#{transDateFr},'DD/MM/YYYY') AND A.TRNSC_DT < TO_DATE(#{transDateTo},'DD/MM/YYYY') + 1
AND B.F_TRNSC_REM = (CASE WHEN #{payType} = 105 THEN 'CSH' WHEN #{payType} = 106 THEN 'CHQ' WHEN #{payType} = 107 THEN 'CRC' WHEN #{payType} = 108 THEN 'ONL' ELSE ' ' END)
AND A.F_BANK_JRNL_ACC_ID = #{bankAcc}
AND B.F_TRNSC_REF_VA_NO = #{vaAccount}
AND B.F_TRNSC_CRDIT_AMT = #{bnkCrAmt}
AND B.OTH_KEYIN_STUS_ID = 1 AND B.OTH_KEYIN_BC_STUS_ID = 1 ORDER BY B.F_TRNSC_ID DESC
