mapper.select:
SELECT EXTENT1.FILE_ID, EXTENT1.FILE_NAME, EXTENT1.TOTAL_RECORD, EXTENT2.NAME STUS, TO_CHAR(EXTENT1.CRT_DT,'DD/MM/YYYY') CRT_DT, EXTENT1.TOT_SUCCES, EXTENT1.TOT_FAIL FROM PAY0302D EXTENT1 JOIN SYS0038M EXTENT2 ON EXTENT2.STUS_CODE_ID = EXTENT1.STUS WHERE 1 = 1
AND EXTENT1.FILE_ID = #{fileId}
AND TO_CHAR(EXTENT1.CRT_DT,'DD/MM/YYYY') >= #{createDt1}
AND TO_CHAR(EXTENT1.CRT_DT,'DD/MM/YYYY') <= #{createDt2}
ORDER BY FILE_ID DESC
