mapper.select:
SELECT A.F_TRNSC_ID id, C.NAME BANK, D.ACC_DESC BANK_ACC_NAME, A.F_TRNSC_DT TRNSC_DT, A.F_TRNSC_REF_CHQ_NO CHQ_NO, A.F_TRNSC_REF_VA_NO, A.F_TRNSC_REF3 REF3, A.F_TRNSC_REF1 REF1, A.F_TRNSC_REF4 REF4, A.F_TRNSC_REF2 REF2, A.F_TRNSC_REF6 REF6, A.F_TRNSC_REM TYPE, A.F_TRNSC_DEBT_AMT DEBT, A.F_TRNSC_CRDIT_AMT CRDIT,
(CASE WHEN A.OTH_KEYIN_STUS_ID = 4 THEN 'Mapped' ELSE 'UnMapped' END) STUS, A.OTH_KEYIN_MAPPING_DT, A.F_TRNSC_TELLER_ID, A.F_TRNSC_NEW_CHQ_NO
,F.MEM_CODE
FROM PAY0081D A JOIN PAY0079D B on A.F_BANK_JRNL_ID = B.F_BANK_JRNL_ID LEFT JOIN SYS0004M C on B.BANK_ID = C.BANK_ID LEFT JOIN SYS0001M D on B.F_BANK_JRNL_ACC_ID = D.ACC_ID
JOIN ORG0001D F ON F.MEM_VA_NO = A.F_TRNSC_REF_VA_NO JOIN ORG1001V E ON E.MEM_ID = F.MEM_ID
AND E.DEPT_CODE = #{deptCode}
AND E.GRP_CODE = #{grpCode}
AND E.ORG_CODE = #{orgCode}
WHERE A.F_TRNSC_REM IN ('ONL','CSH','CHQ')
AND A.F_TRNSC_DT >= TO_DATE(#{bankDateFr}, 'DD/MM/YYYY') AND A.F_TRNSC_DT < TO_DATE(#{bankDateTo}, 'DD/MM/YYYY') + 1
AND A.F_TRNSC_REM = #{searchPayType}
AND B.F_BANK_JRNL_ACC_ID = #{searchBankAcc}
AND A.F_TRNSC_REF_VA_NO = #{searchVa}
AND TO_NUMBER(A.F_TRNSC_CRDIT_AMT) >= #{searchCreditAmtFr}
AND TO_NUMBER(A.F_TRNSC_CRDIT_AMT) <= #{searchCreditAmtTo}
AND A.F_TRNSC_REF_VA_NO = #{searchVa}
