FROM PAY0252T A JOIN PAY0064D C ON A.PAY_ID = C.PAY_ID JOIN PAY0065D D ON A.PAY_ITM_ID = D.PAY_ITM_ID LEFT JOIN SAL0001D E ON C.SALES_ORD_ID = E.SALES_ORD_ID JOIN SYS0013M F ON D.PAY_ITM_MODE_ID = F.CODE_ID LEFT JOIN SYS0038M G ON A.REV_STUS_ID = G.STUS_CODE_ID LEFT JOIN PAY0007D H ON C.BILL_ID = H.BILL_ID LEFT JOIN SAL0001D I ON H.BILL_SO_ID = I.SALES_ORD_ID JOIN PAY0345d DET ON DET.GROUP_SEQ = A.GROUP_SEQ AND DET.PAY_ID = A.PAY_ID
WHERE A.GROUP_SEQ IN
#{item}
AND DET.REQ_ID IN
#{item}
AND DET.REQ_ID = #{reqId}
GROUP BY A.GROUP_SEQ, A.LDGR_TYPE, F.CODE_NAME, E.SALES_ORD_NO, E.CUST_ID, I.CUST_ID, I.SALES_ORD_NO, C.OR_NO, C.BRNCH_ID, A.CRC_STATE_MAPPING_ID, A.CRC_STATE_MAPPING_DT, A.BANK_STATE_MAPPING_ID, A.BANK_STATE_MAPPING_DT, A.REV_STUS_ID, G.NAME, A.REV_DT, C.PAY_ID, A.TOT_AMT ,DET.REQ_ID
ORDER BY A.GROUP_SEQ DESC, C.PAY_ID DESC

SELECT DET.*
FROM PAY0347M MAS
JOIN PAY0348D DET ON MAS.APPV_PRCSS_NO = DET.APPV_PRCSS_NO
WHERE MAS.REQ_ID = #{reqId}
AND DET.APPV_LINE_USER_ID = #{memCode}

SELECT A.REASON_ID, C.RESN_DESC, A.IS_REKEY_IN, A.ATCH_FILE_GRP_ID, A.ATCH_FILE_ID, A.REQ_REMARK, A.OLD_TOTAL_AMT, A.NEW_TOTAL_AMT, A.NEW_PAY_TYPE, PT.CODE_DESC REFUND_TYPE, A.CARD_MODE, A.ISSUE_BANK, BK.NAME ISSUE_BANK_NAME, A.TRX_DATE,
CASE WHEN LENGTH(A.CARD_NO) >= 13 THEN SUBSTR(A.CARD_NO,0,6) || '******' || SUBSTR(A.CARD_NO,13,LENGTH(A.CARD_NO)) ELSE '' END AS CARD_NO, A.CARD_TYPE, A.CARD_BRAND, A.CARD_APPROVAL_NO, A.EXP_DT, A.TENURE, A.REF_NO, A.BANK_CHRG_AMT, A.CARD_HOLDER, A.RUNNG_NO, A.BANK_ACC, A.ITEM_REM, A.TRX_ID, A.TR_NO, A.TR_ISSU_DT, A.COLL_MEM_ID, A.PAY_DT, A.IS_ONLINE, A.IS_LOCK, A.IS_3PARTY, A.STUS_ID, A.IS_FUND_TRNSF, A.SKIP_RECON, A.PAY_ITM_CARD_TYPE, A.PAY_ITM_CARD_MODE, A.CHQ_NO, A.CHQ_DEP_RECIPT_NO, A.SLIP_NO, A.EFT, A.THRD_PARTY_IC, A.BANK_BRNCH_ID, A.GRP_ID, A.PAYER_NM, A.REF_DTL, A.BANK_TYPE, A.VA_ACC, A.IS_COMM_CHK, A.MERCHANT_BANK, A.DCF_ECC_IF_DT, A.KEY_IN_BRNCH, B.APPV_PRCSS_NO AS APPV_PRCSS_NO, B.REQST_USER_ID, D.USER_FULL_NAME, D.USER_NAME, TO_CHAR(A.CRT_DT, 'DD/MM/YYYY') AS REF_CRT_DT FROM PAY0344M A JOIN PAY0347M B ON A.REQ_ID = B.REQ_ID JOIN SYS0032M C ON C.RESN_ID = A.REASON_ID LEFT JOIN SYS0047M D ON D.USER_NAME = B.REQST_USER_ID LEFT JOIN SYS0013M PT ON PT.CODE = A.NEW_PAY_TYPE AND PT.CODE_MASTER_ID = 395 LEFT JOIN SYS0004M BK ON BK.BANK_ID = A.ISSUE_BANK AND BK.STUS_CODE_ID = 1 WHERE 1=1
AND A.REQ_ID = #{reqNo}

