mapper.select:
SELECT TOKEN_ID ,EXT1.CARD_TYPE AS CARD_TYPE ,EXT1.CREDIT_CARD_TYPE AS CREDIT_CARD_TYPE ,EXT1.RESPONSE_CODE ,EXT1.RESPONSE_DESC AS RESPONSE_DESC ,TO_CHAR(EXT1.UPD_DT,'DD/MM/YYYY')AS UPD_DT ,EXT1.UPD_USER_ID AS UPD_USER_ID ,EXT1.CUST_CRC_ID AS CUST_CRC_ID ,EXT2.CODE AS CODE ,EXT1.REMARK AS REMARK FROM PAY0318D EXT1 LEFT JOIN SYS0038M EXT2 ON EXT2.STUS_CODE_ID = EXT1.STATUS WHERE 1 = 1 AND STATUS = '1'
AND RESPONSE_CODE = #{responseCode}
AND CREDIT_CARD_TYPE = #{creditCardType}
ORDER BY EXT1.CUST_CRC_ID ASC

mapper.update:
MERGE INTO PAY0318D D USING ( SELECT #{uploadStatus} AS STATUS ,#{userId} AS USER_ID ,V.TOKEN_ID AS NEW_TOKEN_ID ,C.CUST_CRC_ID AS NEW_CUST_CRC_ID ,V.CARD_TYPE AS NEW_CARD_TYPE ,V.CREDIT_CARD_TYPE AS NEW_CREDIT_CARD_TYPE ,V.RESPONSE_CODE AS NEW_RESPONSE_CODE ,V.RESPONSE_DESC AS NEW_RESPONSE_DESC ,V.REMARK AS NEW_REMARK FROM (
SELECT #{item.tokenId} TOKEN_ID ,#{item.cardType} CARD_TYPE ,#{item.creditCardType} CREDIT_CARD_TYPE ,#{item.responseCode} RESPONSE_CODE ,#{item.responseDesc} RESPONSE_DESC ,#{item.remark} REMARK FROM DUAL
) V JOIN SAL0028D C ON C.CUST_CRC_TOKEN = V.TOKEN_ID
JOIN SYS0094M K ON K.CODE = V.RESPONSE_CODE
) S ON (S.NEW_CUST_CRC_ID = D.CUST_CRC_ID) WHEN MATCHED THEN UPDATE SET D.STATUS = S.STATUS , D.UPD_DT = SYSDATE , D.UPD_USER_ID = S.USER_ID , D.REMARK = S.NEW_REMARK WHEN NOT MATCHED THEN INSERT ( SEQUENCE_ID ,TOKEN_ID ,CUST_CRC_ID ,CARD_TYPE ,CREDIT_CARD_TYPE ,RESPONSE_CODE ,RESPONSE_DESC ,STATUS ,REMARK ,CRT_DT ,CRT_USER_ID ,UPD_DT ,UPD_USER_ID ) VALUES ( PAY0318D_SEQUENCE_ID_SEQ.NEXTVAL ,S.NEW_TOKEN_ID ,S.NEW_CUST_CRC_ID ,S.NEW_CARD_TYPE ,S.NEW_CREDIT_CARD_TYPE ,S.NEW_RESPONSE_CODE ,S.NEW_RESPONSE_DESC ,S.STATUS ,S.NEW_REMARK ,SYSDATE ,S.USER_ID ,SYSDATE ,S.USER_ID )

mapper.select:
SELECT EXTENT1.CUST_CRC_TOKEN ,EXTENT2.SALES_ORD_NO ,EXTENT4.UPD_DT AS LAST_UPDATE_DATE ,EXTENT2.TOT_AMT AS AMOUNT ,EXTENT3.NAME AS CUSTOMER_NAME ,CASE WHEN EXTENT4.IS_3RD_PARTY = '1' THEN 'YES' ELSE 'NO' END AS THIRD_PARTY FROM SAL0028D EXTENT1
LEFT JOIN SAL0001D EXTENT2 ON EXTENT1.CUST_ID = EXTENT2.CUST_ID LEFT JOIN SAL0029D EXTENT3 ON EXTENT3.CUST_ID = EXTENT1.CUST_ID LEFT JOIN SAL0074D EXTENT4 ON EXTENT2.SALES_ORD_ID = EXTENT4.SALES_ORD_ID WHERE 1 = 1
AND EXTENT1.CUST_CRC_ID = #{custCrcId}
AND EXTENT1.CUST_CRC_TOKEN = #{tokenId}
ORDER BY 1 ASC
