mapper.select:
SELECT TOKEN_ID ,EXT3.CODE_NAME AS CARD_TYPE ,EXT4.CODE_NAME AS CREDIT_CARD_TYPE ,EXT1.RESPONSE_CODE ,EXT1.RESPONSE_DESC AS RESPONSE_DESC ,TO_CHAR(EXT1.UPD_DT,'DD/MM/YYYY')AS UPD_DT ,EXT1.UPD_USER_ID AS UPD_USER_ID ,EXT1.CUST_CRC_ID AS CUST_CRC_ID ,EXT2.CODE AS CODE ,EXT1.REMARK AS REMARK FROM PAY0318D EXT1 LEFT JOIN SYS0038M EXT2 ON EXT2.STUS_CODE_ID = EXT1.STATUS LEFT JOIN SYS0013M EXT3 ON EXT3.CODE_ID = EXT1.CARD_TYPE LEFT JOIN SYS0013M EXT4 ON EXT4.CODE_ID = EXT1.CREDIT_CARD_TYPE WHERE 1 = 1
AND TOKEN_ID = #{tokenIdSearch}
AND STATUS = #{status}
AND RESPONSE_CODE = #{responseCode}
AND UPPER(CREDIT_CARD_TYPE) = UPPER(#{creditCardType})
ORDER BY EXT1.CUST_CRC_ID ASC

SELECT EXTENT1.CUST_CRC_TOKEN ,EXTENT2.SALES_ORD_NO ,EXTENT4.UPD_DT AS LAST_UPDATE_DATE ,EXTENT2.TOT_AMT AS AMOUNT ,EXTENT3.NAME AS CUSTOMER_NAME ,CASE WHEN EXTENT4.IS_3RD_PARTY = '1' THEN 'YES' ELSE 'NO' END AS THIRD_PARTY FROM SAL0028D EXTENT1
LEFT JOIN SAL0001D EXTENT2 ON EXTENT1.CUST_ID = EXTENT2.CUST_ID LEFT JOIN SAL0029D EXTENT3 ON EXTENT3.CUST_ID = EXTENT1.CUST_ID LEFT JOIN SAL0074D EXTENT4 ON EXTENT2.SALES_ORD_ID = EXTENT4.SALES_ORD_ID WHERE 1 = 1
AND EXTENT1.CUST_CRC_ID = #{custCrcId}
AND EXTENT1.CUST_CRC_TOKEN = #{tokenId}
ORDER BY 1 ASC
