mapper.select:
SELECT A.FILE_ID, A.FILE_NAME, A.PRCSS_STUS_ID, B.NAME AS PRCSS_STUS_DESC, A.PRCSS_STUS_REMARK, A.BATCH_ID, COUNT(C.ID) AS TOTAL, SUM(CASE WHEN C.PRCSS_STUS_ID = 4 THEN 1 ELSE 0 END) AS TOTAL_SUCCESS, SUM(CASE WHEN C.PRCSS_STUS_ID <> 4 THEN 1 ELSE 0 END) AS TOTAL_FAIL, TO_CHAR(A.CRT_DT,'DD/MM/YYYY') AS CRT_DT FROM PAY0355M A JOIN SYS0038M B ON A.PRCSS_STUS_ID = B.STUS_CODE_ID JOIN PAY0354D C ON A.FILE_ID = C.FILE_ID WHERE 1=1
AND A.BATCH_ID = #{batchId}
AND A.FILE_ID = #{fileId}
GROUP BY A.FILE_ID, A.FILE_NAME, A.PRCSS_STUS_ID, B.NAME, A.PRCSS_STUS_REMARK, A.BATCH_ID, TO_CHAR(A.CRT_DT,'DD/MM/YYYY') ORDER BY A.FILE_ID DESC

SELECT A.ID, A.FILE_ID, TO_CHAR(A.PAY_DATE,'DD/MM/YYYY') AS PAY_DATE, A.TRANS_ID, A.AUTH_CODE, A.CC_NO, A.PAY_METHOD, A.SALES_ORD_NO, A.PAY_TYPE_ID, CASE WHEN PAY_TYPE_ID = 1 THEN 'Product Payment' ELSE CASE WHEN PAY_TYPE_ID = 2 THEN 'Membership Payment' ELSE 'BOTH' END END AS PAY_TYPE_DESC, A.AMOUNT, A.REMARKS, A.PAY_STUS_ID, E.NAME AS PAY_STUS_DESC, A.PRCSS_STUS_ID, F.NAME AS PRCSS_STUS_DESC, B.PRCSS_STUS_ID AS MASTER_PRCSS_STUS_ID, G.NAME AS MASTER_PRCSS_STUS_DESC, A.PRCSS_STUS_REMARK, A.BATCH_ID, B.PRCSS_STUS_REMARK AS MASTER_PRCSS_STUS_REMARK, B.FILE_NAME, TO_CHAR(A.CRT_DT,'DD/MM/YYYY') AS CRT_DT FROM PAY0354D A JOIN PAY0355M B ON A.FILE_ID = B.FILE_ID JOIN SYS0038M E ON A.PAY_STUS_ID = E.STUS_CODE_ID JOIN SYS0038M F ON A.PRCSS_STUS_ID = F.STUS_CODE_ID JOIN SYS0038M G ON B.PRCSS_STUS_ID = G.STUS_CODE_ID
AND A.BATCH_ID = #{batchId}
AND A.FILE_ID = #{fileId}
AND A.PRCSS_STUS_ID in
#{item}
ORDER BY A.ID, A.BATCH_ID DESC
