mapper.select:
SELECT STK_ID , C1 , CODE_NAME , PROD_GROUP FROM ( SELECT DISTINCT T1.STK_ID , T1.STK_CODE ||' - '||T1.STK_DESC C1 , T3.CODE_NAME , NVL(T4.IND, '') PROD_GROUP FROM SYS0026M T1 JOIN SYS0013M T3 ON T3.CODE_ID = T1.STK_CTGRY_ID LEFT JOIN SYS0094M T4 ON T4.CODE = T3.CODE WHERE T1.STK_TYPE_ID = 61 AND T1.STUS_CODE_ID = 1 AND T1.STK_CTGRY_ID in ( 54, -- WATER PURIFIER 55, -- AIR PURIFIER 56, -- BIDET 57, -- SOFTENER 400, -- POINT OF ENTRY 5706, -- MATTRESS 5707 -- FRAME )
AND T1.STK_CTGRY_ID = #{stkCtgryId}
UNION ( SELECT 16451646 STK_ID, '800001-800002 - PRIME MATTRESS KING' C1, 'Mattress Set/Mattress Only' CODE_NAME, 'HOMECARE' PROD_GROUP FROM DUAL UNION SELECT 16471648 STK_ID, '800003-800004 - PRIME MATTRESS QUEEN' C1, 'Mattress Set/Mattress Only' CODE_NAME, 'HOMECARE' PROD_GROUP FROM DUAL UNION SELECT 16491650 STK_ID, '800005-800006 - PRIME MATTRESS SUPER SINGLE' C1, 'Mattress Set/Mattress Only' CODE_NAME, 'HOMECARE' PROD_GROUP FROM DUAL UNION SELECT 20002001 STK_ID, '800011-800012 - PRIME II MATTRESS KING' C1, 'Mattress Set/Mattress Only' CODE_NAME, 'HOMECARE' PROD_GROUP FROM DUAL UNION SELECT 20022003 STK_ID, '800013-800014 - PRIME II MATTRESS QUEEN' C1, 'Mattress Set/Mattress Only' CODE_NAME, 'HOMECARE' PROD_GROUP FROM DUAL )
) ORDER BY PROD_GROUP DESC, CODE_NAME ASC, STK_ID ASC
