mapper.select:
SELECT BATCH_ID, ORD_NO, CUST_IC, CUST_NAME, PRCSS, FICO_SCRE, BATCH_NO, RESULT_RAW, CTOS_DT, UPD_USER_ID, CTOS_BANKRUPT, ORD_ID, CUST_ID, MEM_ID FROM SAL0107D WHERE 1 = 1
AND BATCH_ID = #{batchId}
AND ORD_NO = #{ordNo}
AND PRCSS <> 0 AND RESULT_RAW IS NOT NULL AND ROWNUM <= 1

{
    call SP_UPD_CTOS_FICO_UPD_RERUN (#{userId},
    #{p1, mode=OUT, jdbcType=INTEGER, javaType=int, resultMap=reuploadB2BMap1},
    #{p2, mode=OUT, jdbcType=VARCHAR, javaType=String, resultMap=reuploadB2BMap2})
 }

mapper.update:
UPDATE SYS0094M
    SET CODE = ${towerChannel}, UPD_DT = SYSDATE, UPD_BY = ${userId}
WHERE IND = 'CCP_FLAG'

UPDATE SYS0094M
    SET CODE = ${ageGroup}, UPD_DT = SYSDATE, UPD_BY = ${userId}
WHERE IND = 'CCP_AGE'
