mapper.select:
SELECT Extent1.CNFM_CNTRCT_ID , Extent2.SRV_CNTRCT_ID , Extent2.SRV_CNTRCT_REF_NO , Extent2.SRV_CNTRCT_ORD_ID , Extent6.SALES_ORD_NO , Extent2.SRV_CNTRCT_BRNCH_ID , Extent3.NAME , Extent4.USER_NAME , Extent2.SRV_CNTRCT_CRT_DT , Extent5.USER_NAME USERNAME1 , Extent1.CNFM_UPD_DT , Extent1.CNFM_FDBCK_ID , Extent9.RESN_DESC , Extent3.REGN_ID , Extent1.CNFM_REM , Extent1.CNFM_STUS_ID , Extent8.CODE , Extent7.NAME NAME1, TO_CHAR(Extent2.SRV_CNTRCT_CRT_DT, 'DD-MM-YYYY AM HH:MI:SS ') || '(' || Extent4.USER_NAME || ')' KEY_AT_BY, TO_CHAR(Extent1.CNFM_UPD_DT, 'DD-MM-YYYY AM HH:MI:SS ') || '(' || Extent5.USER_NAME || ')' UPDATE_AT_BY FROM SAL0079D Extent1 JOIN SAL0077D Extent2 ON Extent2.SRV_CNTRCT_ID = Extent1.CNFM_CNTRCT_ID JOIN SYS0005M Extent3 ON Extent3.BRNCH_ID = Extent2.SRV_CNTRCT_BRNCH_ID JOIN SYS0047M Extent4 ON Extent4.USER_ID = Extent2.SRV_CNTRCT_CRT_USER_ID JOIN SYS0047M Extent5 ON Extent5.USER_ID = Extent1.CNFM_UPD_USER_ID JOIN SAL0001D Extent6 ON Extent6.SALES_ORD_ID = Extent2.SRV_CNTRCT_ORD_ID JOIN SAL0029D Extent7 ON Extent7.CUST_ID = Extent6.CUST_ID JOIN SYS0038M Extent8 ON Extent8.STUS_CODE_ID = Extent1.CNFM_STUS_ID LEFT JOIN SYS0032M Extent9 ON Extent9.RESN_ID = Extent1.CNFM_FDBCK_ID
WHERE 1 = 1
AND UPPER(Extent2.SRV_CNTRCT_REF_NO) = UPPER(#{refNo})
AND Extent2.SRV_CNTRCT_CRT_DT >= TO_DATE(#{sDate}||' 00:00:00', 'DD-MM-YYYY HH24:MI:SS')
AND Extent2.SRV_CNTRCT_CRT_DT <= TO_DATE(#{eDate} ||' 23:59:59', 'DD-MM-YYYY HH24:MI:SS')
AND Extent1.CNFM_STUS_ID IN
#{value}
AND Extent6.SALES_ORD_NO = #{salesOrdNo}
AND Extent2.SRV_CNTRCT_BRNCH_ID IN
#{value}
AND UPPER(Extent7.NRIC) = UPPER(#{nric})
AND UPPER(Extent7.NAME) LIKE '%' || UPPER(#{custName}) || '%'
AND Extent7.TYPE_ID = #{custType}
AND Extent3.REGN_ID = #{region}
AND Extent1.CNFM_FDBCK_ID IN
#{value}
AND UPPER(Extent5.USER_NAME) = UPPER(#{updUser})
ORDER BY Extent2.SRV_CNTRCT_ID DESC

mapper.update:
UPDATE SAL0077D
SET SRV_CNTRCT_STUS_ID = #{srvCntrctStusId},
       SRV_CNTRCT_UPD_DT = SYSDATE,
       SRV_CNTRCT_UPD_USER_ID = #{userId}
WHERE   SRV_CNTRCT_ID = #{updCntrcId}

UPDATE SAL0077D
SET SRV_CNTRCT_STUS_ID = #{updMemStatus},
       SRV_CNTRCT_UPD_DT = SYSDATE,
       SRV_CNTRCT_UPD_USER_ID = #{userId}
WHERE   SRV_CNTRCT_ID = #{updCntrcId}

UPDATE SAL0078D
SET CNTRCT_RENTAL_STUS = #{rentalStus},
       CNTRCT_UPD_DT = SYSDATE,
       CNTRCT_UPD_USER_ID = #{userId}
WHERE   CNTRCT_ID = #{subContractId}

UPDATE SAL0088D
SET SRV_PRD_STUS_ID = #{srvPrdStusIdentify},
       SRV_PRD_UPD_DT = SYSDATE,
       SRV_PRD_UPD_USER_ID = #{userId}
WHERE   SRV_PRD_ID = #{srvPrdId}
