SELECT A.CUST_ID , NVL(B.CHS_STUS,'-') CHS_STATUS , CASE WHEN B.CHS_RSN IS NULL OR B.CHS_STUS = 'GREEN' THEN '-' ELSE B.CHS_RSN END CHS_RSN , A.NAME CUST_NAME , C.APPV_REQ , TRUNC(MONTHS_BETWEEN(SYSDATE, A.CRT_DT)) AS CUST_MONTH FROM SAL0029D A LEFT JOIN SAL0262D B ON A.CUST_ID = B.CUST_ID AND CHS_YEAR = TO_CHAR(SYSDATE, 'YYYY') AND CHS_MONTH = TO_CHAR(SYSDATE, 'MM') LEFT JOIN SAL0344D C ON B.CHS_RSN = C.CHS_RSN WHERE 1=1 -->
AND A.NRIC = #{selectKeyword}
AND ROWNUM = 1
