mapper.select:
SELECT A.PRECCP_SEQ , A.CUST_NAME , A.CUST_IC , A.CUST_MOBILENO , A.CUST_EMAIL , TO_CHAR(A.CRT_DT,'YYYY/MM/DD') CRT_DT , CASE WHEN A.CHS_STATUS = 'GREEN' THEN A.CHS_STATUS WHEN A.CHS_STATUS = 'YELLOW' THEN A.CHS_STATUS || '_' || A.CHS_RSN ELSE '-' END CHS_STATUS , A.CUST_ID FROM SAL0343D A LEFT JOIN SYS0047M B ON A.CRT_ID = B.USER_ID WHERE DISAB = 0
AND TO_CHAR(CRT_DT, 'YYYYMMDD') BETWEEN #{reqstDtFrom} AND #{reqstDtTo}
AND B.USER_NAME = #{regId}
AND CUST_NAME LIKE '%' || #{selectKeyword} || '%'
AND CUST_IC LIKE '%' || #{selectKeyword} || '%'
