ACE_ID AS RACE_ID, D1.CODE_NAME2 AS CODE_NAME2, D1.STUS_CODE_ID AS STUS_CODE_ID, D1.UPD_USER_ID AS UPD_USER_ID, D1.UPD_DT AS UPD_DT, D1.USER_NAME2 AS USER_NAME2, D1.CUST_ADD_ID AS CUST_ADD_ID, D1.CUST_CNTC_ID AS CUST_CNTC_ID, D1.CUST_CARE_CNT_ID AS CUST_CARE_CNT_ID, D1.CUST_VA_NO AS CUST_VA_NO, D1.GST_RGIST_NO AS GST_RGIST_NO, D1.CUST_TIER AS CUST_TIER, D1.RECEIVING_MARKETING_MSG_STATUS, D1.CUST_STATUS_ID, D1.CUST_STATUS FROM ( SELECT DISTINCT S1.CUST_ID1 AS CUST_ID, S1.NAME1 AS NAME1, S1.NRIC1 AS NRIC, S1.REM, S1.TYPE_ID AS TYPE_ID, S1.PAS_SPORT_EXPR AS PAS_SPORT_EXPR, S1.VISA_EXPR AS VISA_EXPR, 1 AS C1, CASE WHEN (S1.CRT_USER_ID1 IS NOT NULL) THEN S1.CRT_USER_ID1 ELSE 0 END AS CRT_USER_ID, CASE WHEN (S1.USER_ID1 IS NOT NULL) THEN S1.USER_NAME1 ELSE '' END AS USER_NAME1, CASE WHEN ((S1.CRT_DT1 IS NOT NULL) AND (S1.CRT_DT1 > TO_DATE('1900-01-01','YYYY-MM-DD'))) THEN S1.CRT_DT1 ELSE TO_DATE('1900-01-01','YYYY-MM-DD') END AS CRT_DT, CASE WHEN (S1.CORP_TYPE_ID IS NOT NULL) THEN S1.CORP_TYPE_ID ELSE 0 END AS CORP_TYPE_ID, CASE WHEN (T10.CODE_ID IS NOT NULL) THEN T10.Code ELSE '' END AS CODE, CASE WHEN (T10.CODE_ID IS NOT NULL) THEN T10.CODE_NAME ELSE '' END AS CODE_NAME, CASE WHEN (S1.CODE_ID1 IS NOT NULL) THEN S1.CODE_NAME1 ELSE '' END AS CODE_NAME1, CASE WHEN (964 <> S1.TYPE_ID) THEN TO_DATE('1900-01-01','YYYY-MM-DD') WHEN ((S1.DOB1 IS NOT NULL) AND (S1.DOB1 > TO_DATE('1900-01-01','YYYY-MM-DD'))) THEN S1.DOB1 ELSE TO_DATE('1900-01-01','YYYY-MM-DD') END AS DOB, CASE WHEN (S1.EMAIL1 IS NOT NULL) THEN S1.EMAIL1 ELSE '' END AS EMAIL, CASE WHEN (964 <> S1.TYPE_ID) THEN '' WHEN (S1.GENDER1 IS NOT NULL) THEN S1.GENDER1 ELSE '' END AS GENDER, CASE WHEN (964 <> S1.TYPE_ID) THEN 0 WHEN (S1.NATION IS NOT NULL) THEN S1.NATION ELSE 0 END AS NATION, CASE WHEN (964 <> S1.TYPE_ID) THEN '' WHEN (S1.CNTY_ID1 IS NOT NULL) THEN S1.NAME2 ELSE '' END AS NAME2, CASE WHEN (964 <> S1.TYPE_ID) THEN 0 WHEN (S1.RACE_ID1 IS NOT NULL) THEN S1.RACE_ID1 ELSE 0 END AS RACE_ID, CASE WHEN (964 <> S1.TY