AND CODE_MASTER_ID = #{groupCode}
AND CODE NOT IN (#{notin})
ORDER BY ${orderValue}
ORDER BY CODE_NAME

SELECT COUNT(DISTINCT(NRIC)) CNT FROM SAL0029D A INNER JOIN ( SELECT A1.CUST_ID, A1.CUST_ORI_CRC_NO, B1.CNT FROM SAL0028D A1 INNER JOIN ( SELECT CUST_ORI_CRC_NO, COUNT(*) CNT FROM SAL0028D GROUP BY CUST_ORI_CRC_NO ) B1 ON A1.CUST_ORI_CRC_NO = B1.CUST_ORI_CRC_NO WHERE A1.CUST_ORI_CRC_NO LIKE #{cardNo} AND A1.CUST_CRC_STUS_ID = '1' AND A1.CUST_CRC_TOKEN = #{token}
