) B ON A.CUST_ID = B.CUST_ID
WHERE A.NRIC = #{nric}

WITH S AS ( SELECT * FROM ( SELECT S.CUST_ID , CASE WHEN S.SALES_ORD_NO IS NOT NULL THEN 'X' ELSE NULL END ISVALID , S.STUS_CODE_ID , CCP.CCP_STUS_ID , I.STUS_CODE_ID INS_STUS FROM SAL0001D S LEFT JOIN SAL0102D CCP ON CCP.CCP_SALES_ORD_ID = S.SALES_ORD_ID LEFT JOIN ( SELECT * FROM (SELECT SALES_ORD_ID, STUS_CODE_ID, ROW_NUMBER() OVER (PARTITION BY SALES_ORD_ID ORDER BY INSTALL_ENTRY_ID desc) ROWN FROM SAL0046D ) WHERE ROWN = 1 ) I ON I.SALES_ORD_ID = S.SALES_ORD_ID
