mapper.select:
SELECT A.TICKET_NO, A.ORDER_ID, A.ORDER_NO, A.CUST_ID, A.CUST_NAME, CASE WHEN (A.STATUS = 'P') THEN 'Active' WHEN (A.STATUS = 'A') THEN 'Approve' WHEN (A.STATUS = 'R') THEN 'Rejected' END AS STATUS, A.STATUS STATUS_CODE, A.CNTC_ID, A.REQST_DT, A.OLD_HP_NO, A.OLD_HOME_NO, A.OLD_OFFICE_NO, A.OLD_EMAIL, A.NEW_HP_NO, A.NEW_HOME_NO, A.NEW_OFFICE_NO, A.NEW_EMAIL, BRCH.NAME POSTING_BRCH, A.DEPT_CODE, A.ORG_CODE, A.GRP_CODE, A.REM, A.CRT_DT, CUSR.USER_NAME CRT_USER_ID, A.UPD_DT, TO_CHAR( A.UPD_DT, 'dd/mm/yyyy HH24:MI:SS AM') || ' (' || UUSR.USER_NAME || ')' UPD_USER_ID FROM SAL0329D A JOIN SYS0047M CUSR ON CUSR.USER_ID = A.CRT_USER_ID JOIN SYS0047M UUSR ON UUSR.USER_ID = A.UPD_USER_ID JOIN SYS0005M BRCH ON BRCH.BRNCH_ID = A.POSTING_BRCH WHERE 1=1
AND A.STATUS IN
#{item}
AND A.POSTING_BRCH = #{branch}
AND UPPER(A.CUST_NAME) like '%'|| UPPER(#{custName}) || '%'
AND A.ORDER_NO = #{ordNo}
AND A.DEPT_CODE = #{deptCode}
AND A.ORG_CODE = #{orgCode}
AND A.GRP_CODE = #{grpCode}
AND A.CRT_DT >= TO_DATE(#{reqStartDt}, 'DD/MM/YYYY')
AND A.CRT_DT < TO_DATE(#{reqEndDt}, 'DD/MM/YYYY') + 1

mapper.update:
  UPDATE SAL0329D SET
  STATUS = #{statusCode} ,
  UPD_DT = SYSDATE,
  UPD_USER_ID = #{userId}
  WHERE TICKET_NO = #{ticketNo}

  UPDATE SAL0027D SET
  
  TEL_M1 = #{newHpNo} ,
  
  
  TEL_R = #{newHomeNo},
  
  
  TEL_O = #{newOfficeNo},
  
  
  EMAIL = #{newEmail},
  
  UPD_DT = SYSDATE,
  UPD_USER_ID = #{userId}
  WHERE CUST_CNTC_ID = #{cntcId}
  AND CUST_ID = #{custId}
