/* [com.coway.trust.biz.sales.customerApi.impl.CustomerApiMapper.selectCustomerList] 20190916 - KR JAEMAJEM */ SELECT CUST_ID , NAME , ADDR , TYPE_ID_NAME , CUST_ADD_ID FROM ( SELECT A.CUST_ID AS CUST_ID , A.NAME AS NAME , B.ADDR_DTL ||' '|| NVL(B.STREET, '') ||' '|| NVL(C.AREA, '') ||' '|| NVL(C.POSTCODE, '') ||' '|| NVL(C.CITY,'') ||' '|| NVL(C.STATE,'') ||' '|| NVL(C.COUNTRY,'') AS ADDR , CASE WHEN E.CODE_NAME IS NOT NULL THEN D.CODE_NAME||'('||E.CODE_NAME||')' ELSE D.CODE_NAME END AS TYPE_ID_NAME , B.CUST_ADD_ID AS CUST_ADD_ID , ROW_NUMBER() OVER(PARTITION BY B.CUST_ID ORDER BY B.CUST_ADD_ID DESC)AS ROW_NUMBER FROM SAL0029D A -- ASIS_DB : WebDB ASIS_SCHEMA : dbo ASIS_TABLE : Customer INNER JOIN SAL0023D B -- ASIS_DB : WebDB ASIS_SCHEMA : dbo ASIS_TABLE : CustAddress ON A.CUST_ID = B.CUST_ID AND B.STUS_CODE_ID = 9 --9(MAIN) INNER JOIN SYS0064M C ON B.AREA_ID = C.AREA_ID INNER JOIN SYS0013M D ON A.TYPE_ID = D.CODE_ID LEFT OUTER JOIN SYS0013M E ON A.CORP_TYPE_ID = E.CODE_ID AND A.CORP_TYPE_ID != 0 WHERE A.STUS_CODE_ID != 8
AND A.TYPE_ID = #{typeId}
AND UPPER(A.NAME) LIKE UPPER(#{selectKeyword})||'%'
AND UPPER(A.NRIC) = UPPER(#{selectKeyword})
AND UPPER(A.NRIC) = UPPER(#{selectKeyword})
) WHERE ROW_NUMBER = 1

/* [com.coway.trust.biz.sales.customerApi.impl.CustomerApiMapper.selectNricNoCheck] 20190916 - KR JAEMAJEM */
SELECT  COUNT(1)
FROM    SAL0029D
WHERE   STUS_CODE_ID IN ('1', '9')
AND     TYPE_ID = #{typeId}
AND     TRIM(NRIC) = TRIM(#{nric})
