INSERT INTO SAL0340D ( KEY_IN_ID , KEY_IN_NO , YEAR , MONTH , WEEK , KEYIN_START_DT , KEYIN_END_DT , CRT_USER_ID , CRT_DT ) SELECT SAL0340D_KEYIN_ID_SEQ.NEXTVAL, reqstNo, year, month, week, keyInStartDt, keyInEndDt, creator, crtDt FROM (
SELECT #{item.reqstNo} reqstNo , #{item.year} year , #{item.month} month , #{item.week} week , TO_DATE(#{item.keyInStartDt},'dd/mm/yyyy') keyInStartDt , TO_DATE(#{item.keyInEndDt},'dd/mm/yyyy') keyInEndDt , #{creator} creator , SYSDATE crtDt FROM DUAL
)

