mapper.select:

{
      call SP_GET_ORD_MBRSH_FILTER_CHRG ( #{ORD_ID} ,  #{PROMO_ID} ,#{p1, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=mFCMap})
}

SELECT * FROM ( SELECT S.SALES_ORD_ID , S.RENT_INST_NO , TO_CHAR(S.RENT_INST_DT,'yyyymmdd') RENT_INST_DT, S.RENT_INST_TYPE_ID , S.RENT_INST_AMT , S.RENT_INST_INSERT_MONTH , S.RENT_INST_INSERT_YEAR , S.RENT_INST_ID, TO_CHAR(SYSDATE,'yyyymm')||'01' NOW_DATE FROM ( SELECT R.SALES_ORD_ID , R.RENT_INST_NO , R.RENT_INST_DT , R.RENT_INST_TYPE_ID , R.RENT_INST_AMT , R.RENT_INST_INSERT_MONTH , R.RENT_INST_INSERT_YEAR , R.RENT_INST_ID FROM SAL0070D R WHERE R.SALES_ORD_ID = ${ORD_ID}
AND RENT_INST_DT = SYSDATE
) S ORDER BY S.RENT_INST_NO DESC ) WHERE ROWNUM <= 1


{
      call SP_GET_ORD_OTSTND_INFO ( #{ORD_ID} , #{p1, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=mOTSTND_INFO})
}

SELECT a.SRV_MEM_QUOT_ID, TO_CHAR(SRV_MEM_QUOT_ID) AS SRV_MEM_QUOT_ID_TXT, /* 중복방지용 CHAR 형 */ a.SRV_MEM_QUOT_NO, a.SRV_MEM_PAC_AMT, a.SRV_MEM_BS_AMT, NVL(a.SRV_MEM_PAC_AMT,0) + NVL(a.SRV_MEM_BS_AMT,0) AS TOTAL_AMT, a.SRV_DUR, a.SRV_QUOT_VAL_ID, a.SRV_FREQ, c.SALES_ORD_ID, c.SALES_ORD_NO, c.CUST_ID, g.NAME AS CUST_NAME, c.APP_TYPE_ID, e.STK_DESC, f.SRV_MEM_PAC_ID, f.SRV_MEM_DESC FROM SAL0093D a LEFT JOIN PAY0024D b ON a.SRV_MEM_QUOT_ID = b.SRV_MEM_QOTAT_ID LEFT JOIN SAL0001D c ON a.SRV_SALES_ORD_ID = c.SALES_ORD_ID LEFT JOIN SAL0002D d ON c.SALES_ORD_ID = d.SALES_ORD_ID LEFT JOIN SYS0026M e ON d.ITM_STK_ID = e.STK_ID LEFT JOIN SAL0091M f ON a.SRV_MEM_PAC_ID = f.SRV_MEM_PAC_ID JOIN SAL0029D g ON c.CUST_ID = g.CUST_ID WHERE a.SRV_QUOT_STUS_ID = 1 AND b.SRV_MEM_LG_ID IS NULL AND a.SRV_QUOT_VAL_ID <= TRUNC(SYSDATE)
AND c.SALES_ORD_NO = #{orderNo}
AND f.SRV_MEM_PAC_ID = #{membershipType}
AND a.SRV_MEM_QUOT_NO = #{quotationNo}
AND a.SRV_DUR = #{quotationDur}
AND c.CUST_ID = #{customerId}
AND g.NAME = #{customerNm}
AND g.NRIC = #{nric}
AND c.APP_TYPE_ID = #{appType}
ORDER BY c.SALES_ORD_NO ASC
