SELECT ORG_CODE , GRP_CODE , DEPT_CODE , MEM_CODE , TOTAL_EXPIRED , TOTAL_FRESH_EXPIRED , SVM_BY_OWN , SVM_BY_OTHER , EXTRADE_BY_OWN , EXTRADE_BY_OTHER , REJOIN_RATE , UPD_DT FROM SAL0259T WHERE 1 = 1
AND ORG_CODE = #{orgCode}
AND GRP_CODE = #{grpCode}
AND DEPT_CODE = #{deptCode}
AND MEM_CODE = #{memCode}

SELECT
FLOOR(MONTHS_BETWEEN(FN_GET_LAST_DAY_MONTH(SYSDATE),T0.EXPIRY_DT)) EXPIRED_PERIOD , TO_CHAR(T0.EXPIRY_DT,'YYYY/MM/DD') EXPIRY_DT , T1.SALES_ORD_NO , T0.MEM_CODE , T2.NAME MEMBER_NAME , T3.NAME CUST_NAME , T4.TEL_M1 , T4.TEL_R , T4.TEL_O , T5.CODE_NAME CUST_TYPE , NVL((ACCDEBTSUB.ACC_DEBT_SUB_CURR_COLCT * -1),0) COLLECTION , NVL(ACCDEBTSUB.ACC_DEBT_SUB_OPNG_COLCT_TRGET,0) TARGET , T9.STATE INSTALL_STATE , T9.CITY INSTALL_AREA
FROM SAL0245T T0 JOIN SAL0001D T1 ON T1.SALES_ORD_ID = T0.SALES_ORD_ID JOIN ORG0001D T2 ON T2.MEM_ID = T0.MEM_ID JOIN SAL0029D T3 ON T3.CUST_ID = T1.CUST_ID JOIN SAL0027D T4 ON T4.CUST_CNTC_ID = T1.CUST_CNT_ID JOIN SYS0013M T5 ON T5.CODE_ID = T3.TYPE_ID LEFT JOIN PAY0053S ACCDEBTSUB on ACCDEBTSUB.ACC_DEBT_ORD_ID = T0.SALES_ORD_ID JOIN SAL0045D T7 ON T7.SALES_ORD_ID = T1.SALES_ORD_ID JOIN SAL0023D T8 ON T8.CUST_ADD_ID = T7.ADD_ID LEFT JOIN SYS0064M T9 ON T9.AREA_ID = T8.AREA_ID LEFT JOIN CMM0086T EXC ON EXC.SALES_ORD_NO = T1.SALES_ORD_NO AND exc.expiry_month = TO_CHAR(SYSDATE,'MM') AND exc.expiry_YEAR = TO_CHAR(SYSDATE,'YYYY') JOIN SYS0026M STK ON STK.STK_ID = T0.ITM_STK_ID WHERE EXPR_REJOIN_MONTH = TO_CHAR(SYSDATE,'MM') AND EXPR_REJOIN_YEAR = TO_CHAR(SYSDATE,'YYYY') AND TO_CHAR(T0.EXPIRY_DT,'YYYY/MM') <= TO_CHAR(SYSDATE,'YYYY/MM') AND STK.STK_CTGRY_ID <> 924 AND (T0.REN_STUS IS NULL OR T0.REN_STUS NOT IN ('RET','SUS')) AND (T0.CNTRCT_RENTAL_STUS IS NULL OR T0.CNTRCT_RENTAL_STUS NOT IN ('RET','SUS')) AND T0.APP_TYPE_ID IN (66, 67, 68, 142, 143, 1412) AND EXC.SALES_ORD_NO IS NULL
AND T0.APP_TYPE_ID IN
#{item}
AND FLOOR(MONTHS_BETWEEN(FN_GET_LAST_DAY_MONTH(SYSDATE),T0.EXPIRY_DT)) IN
#{item}
AND FLOOR(MONTHS_BETWEEN(FN_GET_LAST_DAY_MONTH(SYSDATE),T0.EXPIRY_DT)) >= #{expPeriod}
AND T3.TYPE_ID = #{custType}
AND T0.ORG_CODE = #{orgCode}
AND T0.GRP_CODE = #{grpCode}
AND T0.DEPT_CODE = #{deptCode}
AND T0.MEM_CODE = #{memCode}
