SELECT T0.ORG_CODE , T0.GRP_CODE , T0.DEPT_CODE , T0.MEM_CODE , CASE WHEN T0.MEM_LVL = 1 THEN 'GCM' WHEN T0.MEM_LVL = 2 THEN 'SCM' WHEN T0.MEM_LVL = 3 THEN 'CM' WHEN T0.MEM_LVL = 4 THEN 'Cody' END MEM_LVL, T0.TOTAL_EXPIRED , T0.TOTAL_FRESH_EXPIRED , CASE WHEN T0.MEM_LVL IN (3,4) THEN NVL(T1.TOTAL_FRESH_EXPIRED,0) WHEN T0.MEM_LVL = 2 THEN NVL(T3.TOTAL_FRESH_EXPIRED,0) WHEN T0.MEM_LVL = 1 THEN NVL(T5.TOTAL_FRESH_EXPIRED,0) END TOTAL_FRESH_EXPIRED_LAST, CASE WHEN T0.MEM_LVL IN (3,4) THEN NVL(T2.TOTAL_FRESH_EXPIRED,0) WHEN T0.MEM_LVL = 2 THEN NVL(T4.TOTAL_FRESH_EXPIRED,0) WHEN T0.MEM_LVL = 1 THEN NVL(T6.TOTAL_FRESH_EXPIRED,0) END TOTAL_FRESH_EXPIRED_LAST2, T0.TOTAL_FRESH_EXPIRED_3M, T0.SVM_BY_OWN , T0.SVM_BY_OTHER , T0.EXTRADE_BY_OWN , T0.EXTRADE_BY_OTHER , T0.REJOIN_RATE_3M REJOIN_RATE, T0.UPD_DT FROM SAL0259T T0 LEFT JOIN SAL0259T T1 ON T1.MEM_CODE = T0.MEM_CODE AND T1.DEPT_CODE = T0.DEPT_CODE AND T1.REJOIN_YEAR = TO_CHAR(ADD_MONTHS(SYSDATE,-1),'YYYY') AND T1.REJOIN_MONTH = TO_CHAR(ADD_MONTHS(SYSDATE,-1),'MM') AND T1.MEM_LVL = T0.MEM_LVL AND T0.MEM_LVL IN (3,4) LEFT JOIN SAL0259T T2 ON T2.MEM_CODE = T0.MEM_CODE AND T2.DEPT_CODE = T0.DEPT_CODE AND T2.REJOIN_YEAR = TO_CHAR(ADD_MONTHS(SYSDATE,-2),'YYYY') AND T2.REJOIN_MONTH = TO_CHAR(ADD_MONTHS(SYSDATE,-2),'MM') AND T2.MEM_LVL = T0.MEM_LVL AND T0.MEM_LVL IN (3,4) LEFT JOIN SAL0259T T3 ON T3.MEM_CODE = T0.MEM_CODE AND T3.GRP_CODE = T0.GRP_CODE AND T3.DEPT_CODE = T0.DEPT_CODE AND T3.REJOIN_YEAR = TO_CHAR(ADD_MONTHS(SYSDATE,-1),'YYYY') AND T3.REJOIN_MONTH = TO_CHAR(ADD_MONTHS(SYSDATE,-1),'MM') AND T3.MEM_LVL = T0.MEM_LVL AND T0.MEM_LVL = 2 LEFT JOIN SAL0259T T4 ON T4.MEM_CODE = T0.MEM_CODE AND T4.GRP_CODE = T0.GRP_CODE AND T4.DEPT_CODE = T0.DEPT_CODE AND T4.REJOIN_YEAR = TO_CHAR(ADD_MONTHS(SYSDATE,-2),'YYYY') AND T4.REJOIN_MONTH = TO_CHAR(ADD_MONTHS(SYSDATE,-2),'MM') AND T4.MEM_LVL = T0.MEM_LVL AND T0.MEM_LVL = 2 LEFT JOIN SAL0259T T5 ON T5.MEM_CODE = T0.MEM_CODE AND T5.ORG_CODE = T0.ORG_CODE AND T5.GRP_CODE = T0.GRP_CODE AND T5.DEPT_CODE = T0.DEPT_CODE AND T5.REJOIN_YEAR = TO_CHAR(ADD_MONTHS(SYSDATE,-1),'YYYY') AND T5.REJOIN_MONTH = TO_CHAR(ADD_MONTHS(SYSDATE,-1),'MM') AND T5.MEM_LVL = T0.MEM_LVL AND T0.MEM_LVL = 1 LEFT JOIN SAL0259T T6 ON T6.MEM_CODE = T0.MEM_CODE AND T6.ORG_CODE = T0.ORG_CODE AND T6.GRP_CODE = T0.GRP_CODE AND T6.DEPT_CODE = T0.DEPT_CODE AND T6.REJOIN_YEAR = TO_CHAR(ADD_MONTHS(SYSDATE,-2),'YYYY') AND T6.REJOIN_MONTH = TO_CHAR(ADD_MONTHS(SYSDATE,-2),'MM') AND T6.MEM_LVL = T0.MEM_LVL AND T0.MEM_LVL = 1 WHERE 1 = 1 AND T0.MEM_LVL <> 0 AND T0.REJOIN_YEAR = TO_CHAR(SYSDATE,'YYYY') AND T0.REJOIN_MONTH = TO_CHAR(SYSDATE,'MM')
AND T0.ORG_CODE = #{orgCode}
AND T0.GRP_CODE = #{grpCode}
AND T0.DEPT_CODE = #{deptCode}
AND T0.MEM_CODE = #{memCode}
AND T0.MEM_LVL = #{memLvl}
ORDER BY T0.MEM_LVL ASC

SELECT
FLOOR(MONTHS_BETWEEN(FN_GET_LAST_DAY_MONTH(SYSDATE),T0.EXPIRY_DT)) EXPIRED_PERIOD , TO_CHAR(T0.EXPIRY_DT,'YYYY/MM/DD') EXPIRY_DT , T1.SALES_ORD_NO , T0.MEM_CODE , T2.NAME MEMBER_NAME , T3.NAME CUST_NAME , T4.TEL_M1 , T4.TEL_R , T4.TEL_O , T5.CODE_NAME CUST_TYPE , NVL((ACCDEBTSUB.ACC_DEBT_SUB_CURR_COLCT * -1),0) COLLECTION , NVL(ACCDEBTSUB.ACC_DEBT_SUB_OPNG_COLCT_TRGET,0) TARGET , T9.STATE INSTALL_STATE , T9.CITY INSTALL_AREA
FROM SAL0245T T0 JOIN SAL0001D T1 ON T1.SALES_ORD_ID = T0.SALES_ORD_ID JOIN ORG0001D T2 ON T2.MEM_ID = T0.MEM_ID JOIN SAL0029D T3 ON T3.CUST_ID = T1.CUST_ID JOIN SAL0027D T4 ON T4.CUST_CNTC_ID = T1.CUST_CNT_ID JOIN SYS0013M T5 ON T5.CODE_ID = T3.TYPE_ID LEFT JOIN PAY0053S ACCDEBTSUB on ACCDEBTSUB.ACC_DEBT_ORD_ID = T0.SALES_ORD_ID AND ACCDEBTSUB.ACC_DEBT_SRV_CNTRCT_ID = T0.SRV_MEM_CNTRCT_ID JOIN SAL0045D T7 ON T7.SALES_ORD_ID = T1.SALES_ORD_ID JOIN SAL0023D T8 ON T8.CUST_ADD_ID = T7.ADD_ID LEFT JOIN SYS0064M T9 ON T9.AREA_ID = T8.AREA_ID LEFT JOIN CMM0086T EXC ON EXC.SALES_ORD_NO = T1.SALES_ORD_NO AND exc.expiry_month = EXTRACT(MONTH FROM SYSDATE) AND exc.expiry_YEAR = EXTRACT(YEAR FROM SYSDATE) JOIN SYS0026M STK ON STK.STK_ID = T0.ITM_STK_ID WHERE T0.ORD_STUS = 4 AND EXPR_REJOIN_MONTH = TO_CHAR(SYSDATE,'MM') AND EXPR_REJOIN_YEAR = TO_CHAR(SYSDATE,'YYYY') AND TO_CHAR(T0.EXPIRY_DT,'YYYY/MM') <= TO_CHAR(SYSDATE,'YYYY/MM') AND STK.STK_CTGRY_ID <> 924 AND (T0.REN_STUS IS NULL OR T0.REN_STUS NOT IN ('RET','SUS')) AND (T0.CNTRCT_RENTAL_STUS IS NULL OR T0.CNTRCT_RENTAL_STUS NOT IN ('RET','SUS')) AND T0.APP_TYPE_ID IN (66, 67, 68, 142, 143, 1412) AND EXC.SALES_ORD_NO IS NULL AND T2.MEM_TYPE = 2 AND T0.ORG_CODE != ' '
AND T0.APP_TYPE_ID IN
#{item}
AND FLOOR(MONTHS_BETWEEN(FN_GET_LAST_DAY_MONTH(SYSDATE),FN_GET_LAST_DAY_MONTH(T0.EXPIRY_DT))) >= #{expPeriod}
AND FLOOR(MONTHS_BETWEEN(FN_GET_LAST_DAY_MONTH(SYSDATE),FN_GET_LAST_DAY_MONTH(T0.EXPIRY_DT))) IN
#{item}
AND T3.TYPE_ID = #{custType}
AND T0.ORG_CODE = #{orgCode}
AND T0.GRP_CODE = #{grpCode}
AND T0.DEPT_CODE = #{deptCode}
AND T0.MEM_CODE = #{memCode}
