mapper.select:
SELECT MBRSH_ID , MBRSH_NO , ORD_ID , ORD_NO , MBRSH_STUS_ID , MBRSH_STUS_CODE , MBRSH_CRT_USER_ID , MBRSH_CRT_DT , MBRSH_START_DT , MBRSH_EXPR_DT , PAC_NAME , MBRSH_DUR , MBRSH_OTSTND , CUST_NAME , CUST_IC FROM ( SELECT SAL1004V.MBRSH_ID MBRSH_ID, SAL1004V.MBRSH_NO MBRSH_NO, SAL1004V.ORD_ID ORD_ID, SAL1004V.ORD_NO ORD_NO, SAL1004V.MBRSH_STUS_CODE MBRSH_STUS_CODE, SAL1004V.MBRSH_CRT_USER_ID MBRSH_CRT_USER_ID, SAL1004V. MBRSH_CRT_DT MBRSH_CRT_DT, SAL1004V.PAC_NAME PAC_NAME, SAL1004V.MBRSH_DUR MBRSH_DUR, SAL1004V.MBRSH_OTSTND MBRSH_OTSTND, SAL1004V.CUST_NAME CUST_NAME, SAL1004V.CUST_IC CUST_IC, CASE WHEN ( SAL1004V.MBRSH_STUS_ID IS NOT NULL ) THEN SAL1004V.MBRSH_STUS_ID ELSE 0 END MBRSH_STUS_ID , CASE WHEN ( SAL1004V.MBRSH_START_DT IS NOT NULL ) THEN SAL1004V.MBRSH_START_DT ELSE '1900-01-01 00:00:00'END MBRSH_START_DT , CASE WHEN ( SAL1004V.MBRSH_EXPR_DT IS NOT NULL ) THEN SAL1004V.MBRSH_EXPR_DT ELSE '1900-01-01 00:00:00' END MBRSH_EXPR_DT FROM ( SELECT SAL1004V.MBRSH_ID MBRSH_ID , SAL1004V.MBRSH_NO MBRSH_NO , SAL1004V.ORD_ID ORD_ID , SAL1004V.ORD_NO ORD_NO , SAL1004V.MBRSH_STUS_ID MBRSH_STUS_ID , SAL1004V.MBRSH_STUS_CODE MBRSH_STUS_CODE , SAL1004V.MBRSH_CRT_USER_ID MBRSH_CRT_USER_ID , SAL1004V.MBRSH_CRT_DT MBRSH_CRT_DT , SAL1004V.MBRSH_START_DT MBRSH_START_DT , SAL1004V.MBRSH_EXPR_DT MBRSH_EXPR_DT , SAL1004V.PAC_NAME PAC_NAME , SAL1004V.MBRSH_DUR MBRSH_DUR , SAL1004V.MBRSH_OTSTND MBRSH_OTSTND , SAL1004V.CUST_NAME CUST_NAME , SAL1004V.CUST_IC CUST_IC FROM SAL1004V SAL1004V ) SAL1004V WHERE 1=1
AND SAL1004V.MBRSH_STUS_ID IN
#{item}
AND SAL1004V.MBRSH_NO = #{MBRSH_NO}
AND SAL1004V.ORD_NO = #{ORD_NO}
AND SAL1004V.MBRSH_CRT_USER_ID = #{MBRSH_CRT_USER_ID}
AND (SAL1004V.MBRSH_OTSTND > 0)
AND (SAL1004V.MBRSH_OTSTND = 0)
AND (SAL1004V.MBRSH_OTSTND < 0 )
AND to_char(SAL1004V.MBRSH_CRT_DT,'yyyymmdd') = to_char(to_date (#{MBRSH_CRT_DT} ,'DD/MM/YYYY'),'yyyymmdd')
) PJ ORDER BY ORD_ID ASC


{
      call SP_TRACE_ORD_OTSTND( #{ORD_ID},#{p1, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=outOutsMap})
}

