AND CRTUSR.user_name = #{_memCode}
AND CRTUSR.user_brnch_id IN
#{item}
AND ESVM.CRT_DT BETWEEN TO_DATE(#{_reqstStartDt}, 'DD/MM/YYYY') AND TO_DATE(#{_reqstEndDt}, 'DD/MM/YYYY')+1
AND ESVM.STUS IN
#{item, jdbcType=VARCHAR}
AND ESVM.STUS_PROGRESS IN
#{item, jdbcType=VARCHAR}
AND CUST.TYPE_ID IN
#{item}
AND UPPER(CUST.NRIC) = UPPER(#{_nric})
AND UPPER(COVQOU.REF_NO) = UPPER(#{saRefNo})
AND ESVM.CRT_DT BETWEEN TO_DATE(#{_reqstStartDt} || #{_reqstStartTime}, 'dd/mm/yyyy hh24:mi') AND TO_DATE(#{_reqstEndDt} || #{_reqstEndTime}, 'dd/mm/yyyy hh24:mi')
AND UPPER(CUST.NAME) LIKE UPPER(#{custName})||'%'
AND SAL.SALES_ORD_NO = #{ordNo}
AND T9.GRP_CODE = #{grpCode}
AND ESVM.PSM_NO = #{preSalesNopsm}
AND T9.ORG_CODE = #{orgCode}
AND T9.DEPT_CODE = #{deptCode}
AND QUO.SRV_MEM_QUOT_NO = #{quotationNo}
AND CRTUSR.USER_TYPE_ID = #{memType}
AND COVQOU.SRV_MEM_NO = #{membershipNo}
ORDER BY ESVM.PSM_NO

SELECT T.MEM_ID , T.MEM_CODE , T.NAME , T.NRIC , T.MEM_TYPE , T.CODE_NAME , T.DEPT_CODE , T.GRP_CODE , T.ORG_CODE , T.MEM_LVL , T.LVL3_UP_ID , T.LVL2_UP_ID , T.LVL1_UP_ID FROM ( SELECT T1.MEM_ID , T1.MEM_CODE , T1.MEM_TYPE , T1.NAME , T1.NRIC , T2.CODE_NAME , CASE WHEN (NOT((T3.MEM_ID IS NULL) AND (T3.MEM_CODE IS NULL) AND (T3.MEM_LVL IS NULL) AND (T3.DEPT_CODE IS NULL) AND (T3.GRP_CODE IS NULL) AND (T3.ORG_CODE IS NULL) AND (T3.TOP_ORG_CODE IS NULL) AND (T3.MEM_UP_ID IS NULL) AND (T3.LVL3_UP_ID IS NULL) AND (T3.LVL2_UP_ID IS NULL) AND (T3.LVL1_UP_ID IS NULL) AND (T3.LVL0_UP_ID IS NULL))) THEN T3.DEPT_CODE ELSE '' END DEPT_CODE , CASE WHEN (NOT((T3.MEM_ID IS NULL) AND (T3.MEM_CODE IS NULL) AND (T3.MEM_LVL IS NULL) AND (T3.DEPT_CODE IS NULL) AND (T3.GRP_CODE IS NULL) AND (T3.ORG_CODE IS NULL) AND (T3.TOP_ORG_CODE IS NULL) AND (T3.MEM_UP_ID IS NULL) AND (T3.LVL3_UP_ID IS NULL) AND (T3.LVL2_UP_ID IS NULL) AND (T3.LVL1_UP_ID IS NULL) AND (T3.LVL0_UP_ID IS NULL))) THEN T3.GRP_CODE ELSE '' END GRP_CODE , CASE WHEN (NOT((T3.MEM_ID IS NULL) AND (T3.MEM_CODE IS NULL) AND (T3.MEM_LVL IS NULL) AND (T3.DEPT_CODE IS NULL) AND (T3.GRP_CODE IS NULL) AND (T3.ORG_CODE IS NULL) AND (T3.TOP_ORG_CODE IS NULL) AND (T3.MEM_UP_ID IS NULL) AND (T3.LVL3_UP_ID IS NULL) AND (T3.LVL2_UP_ID IS NULL) AND (T3.LVL1_UP_ID IS NULL) AND (T3.LVL0_UP_ID IS NULL))) THEN T3.ORG_CODE ELSE '' END ORG_CODE , CASE WHEN (NOT((T3.MEM_ID IS NULL) AND (T3.MEM_CODE IS NULL) AND (T3.MEM_LVL IS NULL) AND (T3.DEPT_CODE IS NULL) AND (T3.GRP_CODE IS NULL) AND (T3.ORG_CODE IS NULL) AND (T3.TOP_ORG_CODE IS NULL) AND (T3.MEM_UP_ID IS NULL) AND (T3.LVL3_UP_ID IS NULL) AND (T3.LVL2_UP_ID IS NULL) AND (T3.LVL1_UP_ID IS NULL) AND (T3.LVL0_UP_ID IS NULL))) THEN T3.MEM_LVL ELSE 0 END MEM_LVL , CASE WHEN (NOT((T3.MEM_ID IS NULL) AND (T3.MEM_CODE IS NULL) AND (T3.MEM_LVL IS NULL) AND (T3.DEPT_CODE IS NULL) AND (T3.GRP_CODE IS NULL) AND (T3.ORG_CODE IS NULL) AND (T3.TOP_ORG_CODE IS NULL) AND (T3.MEM_UP_ID IS NULL) AND (T3.LVL3_UP