mapper.select:
SELECT STK_ID AS PRODUCT_CODE , C1 AS PRODUCT_NAME FROM ( SELECT DISTINCT T1.STK_ID , T1.STK_CODE ||' - '||T1.STK_DESC C1 , T3.CODE_NAME FROM SYS0026M T1 , SAL0082D T2 , SYS0013M T3 , SAL0016M T4 WHERE T1.STK_ID = T2.SRV_PAC_ITM_PRODUCT_ID AND T1.STK_CTGRY_ID = T3.CODE_ID AND 1 != T1.IS_NCV AND 1 = T2.SRV_PAC_ITM_STUS_ID AND 61 = T1.STK_TYPE_ID AND T2.SRV_CNTRCT_PAC_ID = #{salesSubType} AND T1.STK_ID = T4.STK_ID AND T4.AMT > 0 AND 66 = #{salesType}
AND STK_CODE LIKE '%'||#{searchKeyword}||'%'
AND UPPER(STK_DESC) LIKE '%'||UPPER(#{searchKeyword})||'%'
UNION ALL SELECT DISTINCT T1.STK_ID , T1.STK_CODE ||' - '||T1.STK_DESC C1 , T3.CODE_NAME FROM SYS0026M T1 , SAL0092M T2 , SYS0013M T3 , SAL0016M T4 WHERE T1.STK_ID = T2.SRV_MEM_ITM_STK_ID AND T1.STK_CTGRY_ID = T3.CODE_ID AND 1 != T1.IS_NCV AND 1 = T2.SRV_MEM_ITM_STUS_ID AND 61 = T1.STK_TYPE_ID AND T2.SRV_MEM_PAC_ID = #{srvPacId} AND T1.STK_ID = T4.STK_ID AND T4.AMT > 0 AND 66 != #{salesType}
AND STK_CODE LIKE '%'||#{searchKeyword}||'%'
AND UPPER(STK_DESC) LIKE '%'||UPPER(#{searchKeyword})||'%'
) ORDER BY STK_ID ASC
