mapper.select:
SELECT T.LOG_ID , T.SALES_ORD_ID , T.PRGRS_ID , T.LOG_DT , T.REF_ID , T.IS_LOK , T.LOG_CRT_USER_ID , T.LOG_CRT_DT , T.PRGRS , T.USER_NAME FROM ( SELECT T1.LOG_ID , T1.SALES_ORD_ID , T1.PRGRS_ID , T1.LOG_DT , T1.REF_ID , T1.IS_LOK , T1.LOG_CRT_USER_ID , T1.LOG_CRT_DT , T2.PRGRS , DECODE ( T3.USER_ID, NULL, '', T3.USER_NAME) AS USER_NAME FROM SAL0009D T1 JOIN SAL0010M T2 ON T2.PRGRS_ID = T1.PRGRS_ID LEFT JOIN SYS0047M T3 ON T3.USER_ID = T1.LOG_CRT_USER_ID WHERE T1.SALES_ORD_ID = #{salesOrderId}
AND T2.PRGRS_ID = #{prgrsId}
ORDER BY T1.LOG_ID DESC ) T WHERE ROWNUM <= 1

SELECT ROWNUM , SALES_ORD_ID , CALL_ENTRY_ID , CALL_RESULT_ID , CALL_ROS_AMT , CALL_CRT_DT , CALL_CRT_USER_ID , CALL_REM , TYPE_ID , CODE , CODE_NAME , CALL_FDBCK_ID , RESN_ID , RESN_DESC , CALL_CRT_USER_NAME , STUS_CODE_ID , STUS_CODE , STUS_NAME , ROS_MONTH , ROS_YEAR , ROS_CALLER_USER_ID , ROS_CALLER_USER_NAME , CALL_DT , CALL_TM , INS_NO , INS_STAT , INS_FB , CL_FB
FROM ( SELECT T2.SALES_ORD_ID , T2.CALL_ENTRY_ID , T1.CALL_RESULT_ID , T1.CALL_ROS_AMT , T1.CALL_CRT_DT CALL_CRT_DT_2 , TO_CHAR(T1.CALL_CRT_DT, 'yyyy-MM-dd hh24:mi') CALL_CRT_DT , T1.CALL_CRT_USER_ID , CASE WHEN T13.AS_NO IS NOT NULL AND T4.STUS_CODE_ID = '40' THEN TO_CLOB('1) Pre-AS Register Creator : '|| T13.REG_ID || ' 2) Pre-AS Requestor Remark : ' || T13.REMARK || ' 3) AS Register Remark : ' || T1.CALL_REM) ELSE T1.CALL_REM END AS CALL_REM , T2.TYPE_ID , T3.CODE , T3.CODE_NAME , NVL(T1.CALL_FDBCK_ID, 0) AS CALL_FDBCK_ID , DECODE(T5.RESN_ID, NULL, '', T5.CODE) AS RESN_ID , DECODE(T5.RESN_ID, NULL, '', T5.RESN_DESC) AS RESN_DESC , DECODE(T6.USER_ID, NULL, '', T6.USER_NAME) AS CALL_CRT_USER_NAME , DECODE(T4.STUS_CODE_ID, NULL, 0, T4.STUS_CODE_ID) AS STUS_CODE_ID , DECODE(T4.STUS_CODE_ID, NULL, '', T4.CODE) AS STUS_CODE , DECODE(T4.STUS_CODE_ID, NULL, '', T4.NAME) AS STUS_NAME , CASE WHEN (NOT(T7.SALES_ORD_ID IS NULL AND T7.ROS_YEAR IS NULL AND T7.ROS_MONTH IS NULL)) THEN T7.ROS_MONTH ELSE 0 END AS ROS_MONTH , CASE WHEN (NOT(T7.SALES_ORD_ID IS NULL AND T7.ROS_YEAR IS NULL AND T7.ROS_MONTH IS NULL)) THEN T7.ROS_YEAR ELSE 0 END AS ROS_YEAR , DECODE(T8.USER_ID, NULL, 0, T8.USER_ID) AS ROS_CALLER_USER_ID , DECODE(T8.USER_ID, NULL, '', T8.USER_NAME) AS ROS_CALLER_USER_NAME , TO_CHAR(T1.CALL_CRT_DT, 'dd/mm/yyyy') CALL_DT , TO_CHAR(T1.CALL_CRT_DT, 'hh24:mi:ss') CALL_TM , TO_CHAR(T9.INSTALL_ENTRY_NO) INS_NO , TO_CHAR(T10.CODE) INS_STAT , NVL(M.BRNCH,0) BRANCH , FC.DEFECT_CODE || '-' || FC.DEFECT_DESC INS_FB , T11.CODE || '-' || T11.RESN_DESC CL_FB , M.MEM_CODE , M.BRNCH CURR_BRNCH FROM CCR0007D T1 INNER JOIN CCR0006D T2 ON T2.CALL_ENTRY_ID = T1.CALL_ENTRY_ID INNER JOIN SYS0013M T3 ON T3.CODE_ID = T2.TYPE_ID LEFT OUTER JOIN SYS0038M T4 ON T4.STUS_CODE_ID = T1.CALL_STUS_ID LEFT OUTER JOIN SYS0032M T5 ON (T5.RESN_ID = T1.CALL_FDBCK_ID) AND (439 = T5.RESN_TYPE_ID) LEFT OUTER JOIN SYS0047M T6 ON T6.USER_ID = T1.CALL_CRT_USER_ID LEFT JOIN ORG0001D M ON M.MEM_CODE = T6.HR_CODE LEFT OUTER JOIN MSC0013D T7 ON (T7.SALES_ORD_ID = T2.SALES_ORD_ID) AND TO_NUMBER(T7.ROS_MONTH) = TO_NUMBER(TO_CHAR(T1.CALL_CRT_DT,'MM')) AND TO_NUMBER(T7.ROS_YEAR) = TO_NUMBER(TO_CHAR(T1.CALL_CRT_DT,'YYYY')) LEFT OUTER JOIN SYS0047M T8 ON T8.USER_ID = T7.ROS_CALLER_USER_ID LEFT JOIN SAL0046D T9 ON T9.SALES_ORD_ID = T2.SALES_ORD_ID AND T9.CALL_ENTRY_ID = T2.CALL_ENTRY_ID AND T9.STUS_CODE_ID = 21 LEFT JOIN SAL0047D I ON T9.INSTALL_ENTRY_ID = I.ENTRY_ID LEFT JOIN SYS0100M FC ON I.FAIL_ID = FC.DEFECT_ID and I.FAIL_LOC = FC.DEFECT_GRP LEFT JOIN SYS0038M T10 ON T10.STUS_CODE_ID = T9.STUS_CODE_ID LEFT JOIN SYS0032M T11 ON T1.CALL_FDBCK_ID = T11.RESN_ID LEFT JOIN SAL0001D T12 ON T2.SALES_ORD_ID = T12.SALES_ORD_ID LEFT JOIN SVC0130D T13 ON T12.SALES_ORD_NO = T13.SALES_ORDER_NO AND T13.STUS = 5
WHERE 8 != T1.CALL_STUS_ID AND T2.SALES_ORD_ID = #{salesOrderId}
UNION ALL
SELECT rotA.ROT_ORD_ID AS SALES_ORD_ID, 0 AS CALL_ENTRY_ID, 0 AS CALL_RESULT_ID, 0 AS CALL_ROS_AMT, rotB.ROT_CALL_DT AS CALL_CRT_DT_2, TO_CHAR(rotB.CRT_DT, 'yyyy-MM-dd hh24:mi') AS CALL_CRT_DT, rotB.ROT_CALL_RESPD_ID AS CALL_CRT_USER_ID, TO_CLOB(rotB.ROT_REM) AS CALL_REM, 0 AS TYPE_ID, '' AS CODE, 'ROT REQUEST' AS CODE_NAME, 0 AS CALL_FDBCK_ID, '' AS RESN_ID, '' AS RESN_DESC, s47.USER_NAME AS CALL_CRT_USER_NAME, 0 AS STUS_CODE_ID, rotB.ROT_CALL_STUS AS STUS_CODE, CASE WHEN rotB.ROT_CALL_STUS = 'ACT' THEN 'Active' WHEN rotB.ROT_CALL_STUS = 'VER' THEN 'Verification' WHEN rotB.ROT_CALL_STUS = 'APP' THEN 'Approve' WHEN rotB.ROT_CALL_STUS = 'ONH' THEN 'On-Hold' ELSE '' END AS STUS_NAME, 0 AS ROS_MONTH, 0 AS ROS_YEAR, 0 AS ROS_CALLER_USER_ID, '' AS ROS_CALLER_USER_NAME, '' AS CALL_DT, '' AS CALL_TM, NULL AS INS_NO, NULL AS INS_STAT, 0 AS BRANCH, '-' AS INS_FB, '' AS CL_FB, '' AS MEM_CODE, 0 AS CURR_BRNCH FROM SAL0275M rotA LEFT JOIN SAL0278D rotB ON rotB.ROT_ID = rotA.ROT_ID LEFT JOIN SYS0047M s47 ON s47.USER_ID = rotB.ROT_CALL_RESPD_ID WHERE rotA.ROT_ORD_ID = #{salesOrderId}
ORDER BY CALL_CRT_DT DESC
ORDER BY CALL_RESULT_ID ASC
ORDER BY CALL_CRT_DT DESC
) AAA
WHERE ROWNUM <= 1

SELECT V1.ORD_ID , V1.ADDR_DTL , V1.STREET , V1.MAIL_AREA , V1.MAIL_POST_CODE , V1.MAIL_CITY , V1.MAIL_STATE , V1.MAIL_CNTY , V1.MAIL_CNT_NAME , V1.MAIL_CNT_NRIC , V1.MAIL_CNT_EMAIL , V1.MAIL_CNT_EMAIL_ADD , V1.MAIL_CNT_TEL_M , V1.MAIL_CNT_TEL_O , V1.MAIL_CNT_TEL_R , V1.MAIL_CNT_TEL_F , V1.MAIL_CNT_GENDER , V1.BILL_GRP_NO , V1.BILL_STATE_EMAIL , V1.BILL_STATE , V1.BILL_SMS , V1.BILL_POST , V1.MAIL_CNTC_ID , V1.MAIL_ADDR_ID , V1.MAIL_CNT_DEPT , V1.MAIL_CNT_POST
, V1.E_INV_FLG
FROM SAL1011V V1 WHERE V1.ORD_ID = #{salesOrderId}

{
  call SP_GET_ORD_LAST_6_MTH_TRNSC(#{salesOrderId},#{p1, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=transMap})
}

SELECT * FROM ( SELECT '1' AS SEQ, C.SALES_ORD_NO AS ORDERNO, A.INSTALL_ENTRY_NO AS ID, A.INSTALL_ENTRY_NO AS RID, TO_CHAR(B.INSTALL_DT, 'DD/MM/YYYY') AS COMPLETE_DT, TRIM(NVL(TO_CHAR(B.PSI, '999'), '-')) AS PSI, TRIM(NVL(TO_CHAR(B.LPM, '999'), '-')) AS LPM, E.CODE AS BR_CDE, E.NAME AS BR, D.MEM_CODE AS CT_CDE, D.NAME AS CT_NM, D.TEL_MOBILE AS CT_HP, UPPER(H.AREA) AS INST_AREA, UPPER(H.CITY) AS INST_CITY, H.POSTCODE AS INST_PSTCDE, UPPER(H.STATE) AS INST_STATE, TRIM(NVL(TO_CHAR(B.VOLT, '999'), '-')) AS VOLT, TRIM(NVL(TO_CHAR(B.TDS, '999'), '-')) AS TDS, TRIM(NVL(TO_CHAR(B.ROOM_TEMP, '999'), '-')) AS ROOM_TEMP, TRIM(NVL(TO_CHAR(B.WATER_SRC_TEMP, '999'), '-')) AS WATER_SOURCE_TEMP, TRIM(NVL(I.CODE_DESC, '-')) AS ADPT_USED FROM SAL0046D A JOIN SAL0047D B ON A.INSTALL_ENTRY_ID = B.ENTRY_ID JOIN SAL0001D C ON A.SALES_ORD_ID = C.SALES_ORD_ID JOIN ORG0001D D ON B.CT_ID = D.MEM_ID JOIN SYS0005M E ON D.BRNCH = E.BRNCH_ID LEFT JOIN SAL0045D F ON C.SALES_ORD_ID = F.SALES_ORD_ID LEFT JOIN SAL0023D G ON F.ADD_ID = G.CUST_ADD_ID LEFT JOIN SYS0064M H ON G.AREA_ID = H.AREA_ID LEFT JOIN SYS0013M I ON B.ADPT_USED =I.CODE_ID WHERE B.STUS_CODE_ID = 4 AND C.SALES_ORD_NO = #{ordNo}
UNION ALL
SELECT '2' AS SEQ, C.SALES_ORD_NO AS ORDERNO, A.AS_NO AS ID, B.AS_RESULT_NO AS RID, TO_CHAR(B.AS_SETL_DT, 'DD/MM/YYYY') AS COMPLETE_DT, TRIM(NVL(TO_CHAR(B.PSI, '999'), '-')) AS PSI, TRIM(NVL(TO_CHAR(B.LPM, '999'), '-')) AS LPM, E.CODE AS BR_CDE, E.NAME AS BR, D.MEM_CODE AS CT_CDE, D.NAME AS CT_NM, D.TEL_MOBILE AS CT_HP, UPPER(H.AREA) AS INST_AREA, UPPER(H.CITY) AS INST_CITY, H.POSTCODE AS INST_PSTCDE, UPPER(H.STATE) AS INST_STATE, TRIM('-') AS VOLT, TRIM( '-') AS TDS, TRIM('-') AS ROOM_TEMP, TRIM('-') AS WATER_SOURCE_TEMP, TRIM('-') AS ADPT_USED FROM SVC0001D A JOIN SVC0004D B ON A.AS_ID = B.AS_ENTRY_ID JOIN SAL0001D C ON A.AS_SO_ID = C.SALES_ORD_ID JOIN ORG0001D D ON B.AS_CT_ID = D.MEM_ID JOIN SYS0005M E ON D.BRNCH = E.BRNCH_ID LEFT JOIN SAL0045D F ON C.SALES_ORD_ID = F.SALES_ORD_ID LEFT JOIN SAL0023D G ON F.ADD_ID = G.CUST_ADD_ID LEFT JOIN SYS0064M H ON G.AREA_ID = H.AREA_ID WHERE B.AS_RESULT_STUS_ID = 4 AND B.AS_RESULT_TYPE_ID = '457' AND C.SALES_ORD_NO = #{ordNo}
) A ORDER BY A.SEQ, A.COMPLETE_DT ASC
