, T1.TOT_AMT , T1.PROMO_ID , T1.BINDING_NO , T1.CC_PROMO_ID , NVL(T1.TOT_PV, 0) AS TOT_PV , T1.REM , T1.PV_MONTH , T1.PV_YEAR , T1.STUS_CODE_ID , T1.UPD_DT , T1.UPD_USER_ID , T1.SYNC_CHK , T1.CUST_PO_NO , T1.REN_CHK_ID , T1.INST_PRIOD , T1.DO_NO , T1.DEPT_CODE , T1.GRP_CODE , T1.ORG_CODE , T1.SALES_ORD_ID_OLD , T1.EDIT_TYPE_ID , T1.CUST_BILL_ID , NVL(T1.MTH_RENT_AMT, 0) AS MTH_RENT_AMT , T1.LOK , T1.AEON_STUS_ID , T1.COMM_DT , T1.CRT_USER_ID , T1.CRT_DT , T1.PAY_COM_DT , T1.DEF_RENT_AMT , T1.REF_DOC_ID
, T1.SALES_HM_ID , T1.SALES_SM_ID , T1.SALES_GM_ID , T1.ADV_BILL , T1.CNVR_SCHEME_ID , T1.CUST_CARE_CNT_ID , T1.EMP_CHK , T1.EX_TRADE , T1.ECASH
, T1.PROMO_DISC_PERIOD_TP , T1.PROMO_DISC_PERIOD , NVL(T1.NOR_AMT, 0) AS NOR_AMT , NVL(T1.NOR_RNT_FEE, 0) AS NOR_RNT_FEE , NVL(T1.DISC_RNT_FEE, 0) AS DISC_RNT_FEE , T1.GST_CHK , T2.TYPE_ID , T3.CODE_NAME APP_TYPE_NAME , T4.NAME ORDER_STATUS , NVL(T5.STUS_CODE_ID, '-') RENTAL_STATUS , T6.CNTRCT_RENTAL_PRIOD RENTAL_PERIOD FROM SAL0001D T1 LEFT JOIN SAL0029D T2 ON T2.CUST_ID = T1.CUST_ID LEFT JOIN SYS0013M T3 ON T3.CODE_ID = T1.APP_TYPE_ID LEFT JOIN SYS0038M T4 ON T4.STUS_CODE_ID = T1.STUS_CODE_ID LEFT JOIN SAL0071D T5 ON T5.SALES_ORD_ID = T1.SALES_ORD_ID LEFT JOIN SAL0003D T6 ON T6.CNTRCT_SALES_ORD_ID = T1.SALES_ORD_ID WHERE T1.SALES_ORD_ID = #{salesOrdId}
AND T1.APP_TYPE_ID = #{appTypeId}
AND ROWNUM <= 1

