choose.when:
SELECT T1.SALES_ORD_ID , T1.SALES_ORD_NO , DECODE(T1.SALES_DT, NULL, '19000101', TO_CHAR(T1.SALES_DT, 'YYYYMMDD')) AS ORD_DT , TO_CHAR(T1.SALES_DT, 'DD/MM/YYYY') AS ORD_DT2 , T1.APP_TYPE_ID , NVL(T1.STUS_CODE_ID, 0) AS STUS_CODE_ID , T1.DEPT_CODE , T1.GRP_CODE , T1.ORG_CODE , T2.ITM_STK_ID , T3.REN_SCH_ID , NVL(T3.STUS_CODE_ID, '') STUS_CODE_ID1 , CASE WHEN T4.FRA_ORD_NO = T1.SALES_ORD_NO THEN 3 WHEN T4.MAT_ORD_NO > 0 AND T4.FRA_ORD_NO > 0 THEN 2 WHEN T4.MAT_ORD_NO > 0 THEN 1 ELSE 0 END BNDL_ID , T1.MTH_RENT_AMT FROM SAL0001D T1 JOIN SAL0002D T2 ON T2.SALES_ORD_ID = T1.SALES_ORD_ID LEFT JOIN SAL0071D T3 ON T3.SALES_ORD_ID = T1.SALES_ORD_ID AND 66 = T1.APP_TYPE_ID LEFT JOIN HMC0011D T4 ON T4.ORD_SEQ_NO = T1.BNDL_ID LEFT JOIN ORG1001V T5 ON T5.MEM_ID = T1.MEM_ID WHERE T1.SALES_ORD_NO = #{salesOrdNo} AND ROWNUM <= 1
AND T5.MEM_ID = #{memId}
AND T5.DEPT_CODE = #{deptCode}
AND T5.GRP_CODE = #{grpCode}
AND T5.ORG_CODE = #{orgCode}
UNION
SELECT T1.SALES_ORD_ID , T1.SALES_ORD_NO , DECODE(T1.SALES_DT, NULL, '19000101', TO_CHAR(T1.SALES_DT, 'YYYYMMDD')) AS ORD_DT , TO_CHAR(T1.SALES_DT, 'DD/MM/YYYY') AS ORD_DT2 , T1.APP_TYPE_ID , NVL(T1.STUS_CODE_ID, 0) AS STUS_CODE_ID , T1.DEPT_CODE , T1.GRP_CODE , T1.ORG_CODE , T2.ITM_STK_ID , T3.REN_SCH_ID , NVL(T3.STUS_CODE_ID, '') STUS_CODE_ID1 , CASE WHEN T4.FRA_ORD_NO = T1.SALES_ORD_NO THEN 3 WHEN T4.MAT_ORD_NO > 0 AND T4.FRA_ORD_NO > 0 THEN 2 WHEN T4.MAT_ORD_NO > 0 THEN 1 ELSE 0 END BNDL_ID , T1.MTH_RENT_AMT FROM SAL0001D T1 JOIN SAL0002D T2 ON T2.SALES_ORD_ID = T1.SALES_ORD_ID LEFT JOIN SAL0071D T3 ON T3.SALES_ORD_ID = T1.SALES_ORD_ID AND 66 = T1.APP_TYPE_ID LEFT JOIN HMC0011D T4 ON T4.ORD_SEQ_NO = T1.BNDL_ID JOIN SAL0090D T6 ON T6.SRV_SO_ID = T1.SALES_ORD_ID LEFT JOIN ORG1001V T5 ON T5.MEM_ID = T6.SRV_CODY_ID WHERE T1.SALES_ORD_NO = #{salesOrdNo} AND ROWNUM <= 1
AND T5.MEM_ID = #{memId}
AND T5.DEPT_CODE = #{deptCode}
AND T5.GRP_CODE = #{grpCode}
AND T5.ORG_CODE = #{orgCode}

mapper.select:
SELECT T1.PROMO_ITM_ID , T1.PROMO_ID , T1.PROMO_ITM_STK_ID , T2.STK_CODE AS ITMCD , T2.STK_DESC AS ITMNAME , T1.PROMO_ITM_CUR_ID , T4.AMT PROMO_ITM_PRC , T4.PRC_RPF , T4.PRC_PV , CASE WHEN T4.APP_TYPE_ID IN (67,68) THEN TRUNC(FLOOR(T4.AMT - DECODE(T3.PROMO_DISC_TYPE, 0, T4.AMT*(T3.PROMO_PRC_PRCNT/100), T3.PROMO_PRC_PRCNT) - T3.PROMO_ADD_DISC_PRC),-1) ELSE FLOOR(T4.AMT - DECODE(T3.PROMO_DISC_TYPE, 0, T4.AMT*(T3.PROMO_PRC_PRCNT/100), T3.PROMO_PRC_PRCNT) - T3.PROMO_ADD_DISC_PRC) END AS PROMO_AMT , T4.PRC_RPF - T3.PROMO_RPF_DISC_AMT AS PROMO_PRC_RPF , T1.PROMO_ITM_PV , NVL(T1.PROMO_ITM_PV_GST, 0) AS PROMO_ITM_PV_GST , T3.PROMO_APP_TYPE_ID FROM SAL0018D T1 LEFT OUTER JOIN SYS0026M T2 ON T1.PROMO_ITM_STK_ID = T2.STK_ID LEFT OUTER JOIN SAL0017D T3 ON T1.PROMO_ID = T3.PROMO_ID LEFT OUTER JOIN SAL0016M T4 ON T4.STK_ID = T1.PROMO_ITM_STK_ID AND T4.APP_TYPE_ID = DECODE(T3.PROMO_APP_TYPE_ID, 2284, 66, 2285, 67, 2286, 68, 2287, 1412) WHERE T4.MEM_PAC_ID = 0 AND T1.PROMO_ITM_STUS_ID = 1
AND T1.PROMO_ID = #{promoId}
AND T1.PROMO_ITM_STK_ID = #{promoItmStkId}
AND T3.promo_type_id = #{promoTypeId}
AND T3.promo_app_type_id = #{promoAppTypeId}
AND T3.promo_srv_mem_pac_id = #{promoSrvMemPacId}
AND T3.promo_prc_prcnt = #{promoPrcPrcnt}
AND T3.promo_rpf_disc_amt = #{promoRpfDiscAmt}
AND T3.PROMO_ADD_DISC_PRC = #{promoAddDiscPrc}
AND TO_DATE(T3.PROMO_DT_FROM) <= SYSDATE AND TO_DATE(T3.PROMO_DT_END) >= SYSDATE
AND ROWNUM <= 1

SELECT * FROM ( SELECT T1.PROMO_ITM_ID , T1.PROMO_ID , T1.PROMO_ITM_STK_ID , T2.STK_CODE AS ITMCD , T2.STK_DESC AS ITMNAME , T1.PROMO_ITM_CUR_ID , T4.AMT PROMO_ITM_PRC , T4.PRC_RPF , T4.PRC_PV , CASE WHEN T4.APP_TYPE_ID IN (67,68) THEN TRUNC(FLOOR(T4.AMT - DECODE(T3.PROMO_DISC_TYPE, 0, T4.AMT*(T3.PROMO_PRC_PRCNT/100), T3.PROMO_PRC_PRCNT) - T3.PROMO_ADD_DISC_PRC),-1) ELSE FLOOR(T4.AMT - DECODE(T3.PROMO_DISC_TYPE, 0, T4.AMT*(T3.PROMO_PRC_PRCNT/100), T3.PROMO_PRC_PRCNT) - T3.PROMO_ADD_DISC_PRC) END AS PROMO_AMT , T4.PRC_RPF - T3.PROMO_RPF_DISC_AMT AS PROMO_PRC_RPF , T1.PROMO_ITM_PV , NVL(T1.PROMO_ITM_PV_GST, 0) AS PROMO_ITM_PV_GST , T3.PROMO_APP_TYPE_ID FROM SAL0018D T1 LEFT OUTER JOIN SYS0026M T2 ON T1.PROMO_ITM_STK_ID = T2.STK_ID LEFT OUTER JOIN SAL0017D T3 ON T1.PROMO_ID = T3.PROMO_ID LEFT OUTER JOIN SAL0016M T4 ON T4.STK_ID = T1.PROMO_ITM_STK_ID AND T4.APP_TYPE_ID = DECODE(T3.PROMO_APP_TYPE_ID, 2284, 66, 2285, 67, 2286, 68, 2287, 1412) WHERE T4.MEM_PAC_ID = 0 AND T1.PROMO_ITM_STUS_ID = 1
AND T1.PROMO_ITM_STK_ID = #{promoItmAnoStkId} AND T3.PROMO_DESC LIKE '%CONVERSION%'
AND T3.promo_type_id = #{promoTypeId}
AND T3.promo_app_type_id = #{promoAppTypeId}
AND T3.promo_srv_mem_pac_id = #{promoSrvMemPacId}
AND T3.promo_prc_prcnt = #{promoPrcPrcnt}
AND T3.promo_rpf_disc_amt = #{promoRpfDiscAmt}
AND T3.PROMO_ADD_DISC_PRC = #{promoAddDiscPrc}
AND TO_DATE(T3.PROMO_DT_FROM) <= SYSDATE AND TO_DATE(T3.PROMO_DT_END) >= SYSDATE
ORDER BY 1 DESC) WHERE ROWNUM <= 1

