mapper.select:
SELECT T2.CODE_NAME AS CHANNEL , T1.SOF_NO , T3.CODE_NAME AS APP_TYPE , TO_CHAR(T1.REQST_DT, 'DD/MM/YYYY') AS REQUEST_DT , T4.STK_DESC AS PRODUCT , T5.NAME AS CUST_NM , T6.CODE_NAME AS CUST_TYPE , T5.NRIC , T7.USER_NAME , T8.NAME AS STUS_NAME , T8.STUS_CODE_ID , T1.PRE_ORD_ID FROM SAL0213M T1 LEFT OUTER JOIN SYS0013M T2 ON T2.CODE = T1.CHNNL AND T2.CODE_MASTER_ID = 350 LEFT OUTER JOIN SYS0013M T3 ON T3.CODE_ID = T1.APP_TYPE_ID AND T3.CODE_MASTER_ID = 10 LEFT OUTER JOIN SYS0026M T4 ON T4.STK_ID = T1.ITM_STK_ID LEFT OUTER JOIN SAL0029D T5 ON T5.CUST_ID = T1.CUST_ID LEFT OUTER JOIN SYS0013M T6 ON T6.CODE_ID = T5.TYPE_ID AND T6.CODE_MASTER_ID = 8 LEFT OUTER JOIN SYS0047M T7 ON T7.USER_ID = T1.CRT_USER_ID LEFT OUTER JOIN SYS0038M T8 ON T8.STUS_CODE_ID = T1.STUS_ID WHERE 1 = 1
AND T1.MEM_CODE = #{_memCode}
AND T1.APP_TYPE_ID IN
#{item}
AND TO_CHAR(T1.REQST_DT, 'DD/MM/YYYY') = #{_reqstDt}
AND T8.CODE IN
#{item, jdbcType=VARCHAR}
AND T1.KEYIN_BRNCH_ID IN
#{item}
AND T5.NRIC = #{_nric}
AND T1.SOF_NO = #{_sofNo}
AND T5.TYPE_ID IN
#{item}
AND T5.NAME LIKE #{_name}||'%'
ORDER BY T1.PRE_ORD_ID ASC

