SELECT T2.CODE_NAME AS CHANNEL , T1.SOF_NO , T3.CODE_NAME AS APP_TYPE , TO_CHAR(T1.REQST_DT, 'DD/MM/YYYY') AS REQUEST_DT , TO_CHAR(T1.REQST_DT, 'HH:MI:SS AM') AS REQUEST_TM , T4.STK_DESC AS PRODUCT , T5.NAME AS CUST_NM , T6.CODE_NAME AS CUST_TYPE , T5.NRIC , T7.USER_NAME CRT_NAME , TO_CHAR(T1.UPD_DT, 'DD/MM/YYYY HH:MI:SS AM') || ' (' || T10.USER_NAME || ')' LAST_UPD , T8.NAME AS STUS_NAME , T1.STUS_ID , T1.PRE_ORD_ID , T1.REM1 , T1.REM2 , T9.Org_Code , T9.Grp_Code , T9.Dept_Code , T11.SALES_ORD_NO , T13.AREA , T13.SO_BRNCH_CODE , T12.AREA_ID , T14.PV_MONTH , T14.PV_YEAR , T14.PROMO_CODE , T14.PROMO_DESC , T14.SALES_USER_ID , T14.HP_NAME , T14.ORG_CODE , T14.GRP_CODE , T14.DEPT_CODE , CASE WHEN T15.DOC_SO_ID IS NULL THEN 'N' ELSE 'Y' END DOC_SUBMIT , T15.SUBMIT_BRANCH , T15.BRANCH_LOCATION FROM SAL0213M T1 LEFT JOIN SYS0013M T2 ON T2.CODE = T1.CHNNL AND T2.CODE_MASTER_ID = 350 LEFT JOIN SYS0013M T3 ON T3.CODE_ID = T1.APP_TYPE_ID AND T3.CODE_MASTER_ID = 10 LEFT JOIN SYS0026M T4 ON T4.STK_ID = T1.ITM_STK_ID LEFT JOIN SAL0029D T5 ON T5.CUST_ID = T1.CUST_ID LEFT JOIN SYS0013M T6 ON T6.CODE_ID = T5.TYPE_ID AND T6.CODE_MASTER_ID = 8 LEFT JOIN SYS0047M T7 ON T7.USER_ID = T1.CRT_USER_ID LEFT JOIN SYS0038M T8 ON T8.STUS_CODE_ID = T1.STUS_ID LEFT JOIN ORG1001V T9 ON T9.MEM_CODE = T7.USER_NAME LEFT JOIN SYS0047M T10 ON T10.USER_ID = T1.UPD_USER_ID LEFT JOIN SAL0001D T11 ON T1.SALES_ORD_ID = T11.SALES_ORD_ID LEFT JOIN SAL0023D T12 ON T12.CUST_ADD_ID = T1.INST_ADD_ID LEFT JOIN SYS0064M T13 ON T13.AREA_ID = T12.AREA_ID LEFT JOIN REP0011S T14 ON T14.ORD_NO = T11.SALES_ORD_NO LEFT JOIN ( SELECT DOC.DOC_SO_ID ,BRNCH.CODE SUBMIT_BRANCH, BRNCH.NAME BRANCH_LOCATION FROM ORG0010D DOC JOIN SYS0005M BRNCH ON DOC.DOC_SUB_BRNCH_ID = BRNCH.BRNCH_ID WHERE DOC_TYPE_ID = 3198 AND DOC.STUS_ID = 1 ) T15 ON T15.DOC_SO_ID = T11.SALES_ORD_ID WHERE 1 = 1
AND T1.MEM_CODE = #{_memCode}
AND T1.APP_TYPE_ID IN
#{item}
AND T1.REQST_DT BETWEEN TO_DATE(#{_reqstStartDt}, 'DD/MM/YYYY') AND TO_DATE(#{_reqstEndDt}, 'DD/MM/YYYY')+1
AND T8.CODE IN
#{item, jdbcType=VARCHAR}
AND T1.KEYIN_BRNCH_ID IN
#{item}
AND UPPER(T5.NRIC) = UPPER(#{_nric})
AND UPPER(T1.SOF_NO) = UPPER(#{_sofNo})
AND T5.TYPE_ID IN
#{item}
AND UPPER(T5.NAME) LIKE UPPER(#{_name})||'%'
AND T9.ORG_CODE = #{orgCode}
AND T9.GRP_CODE = #{grpCode}
AND T9.DEPT_CODE = #{deptCode}
ORDER BY T1.PRE_ORD_ID ASC

