mapper.select:
SELECT T2.CODE_NAME AS CHANNEL , T1.SOF_NO , T3.CODE_NAME AS APP_TYPE , TO_CHAR(T1.REQST_DT, 'DD/MM/YYYY') AS REQUEST_DT , TO_CHAR(T1.REQST_DT, 'HH:MI:SS AM') AS REQUEST_TM , T4.STK_DESC AS PRODUCT , T5.NAME AS CUST_NM , T6.CODE_NAME AS CUST_TYPE , T5.NRIC , T7.USER_NAME CRT_NAME , CASE WHEN ( t16.mem_type = 2) THEN 'Cody' WHEN ( t16.mem_type = 1) THEN 'HP' ELSE '' END SALES_USER_ID , TO_CHAR(T1.UPD_DT, 'DD/MM/YYYY HH:MI:SS AM') || ' (' || T10.USER_NAME || ')' LAST_UPD , T8.NAME AS STUS_NAME , T1.STUS_ID , T1.PRE_ORD_ID , T1.REM1 , T1.REM2 , T9.Org_Code , T9.Grp_Code , T9.Dept_Code , T11.SALES_ORD_NO , T13.AREA , T13.SO_BRNCH_CODE , T12.AREA_ID , T11.PV_MONTH , T11.PV_YEAR , T14.PROMO_CODE , T14.PROMO_DESC , T1.MEM_CODE HP_NAME , DECODE(DOC.DOC_SO_ID,NULL,'N','Y') DOC_SUBMIT , BRNCH.CODE SUBMIT_BRANCH , BRNCH.NAME BRANCH_LOCATION , TO_CHAR(T1.UPD_DT,'YYYYMMDDHH24MISS') UPD_DT , T17.CODE || ' - ' || T17.NAME BRANCH_NAME , T13.STATE , RGN.CODE_NAME REGION_NAME FROM SAL0213M T1 JOIN SYS0013M T2 ON T2.CODE = T1.CHNNL AND T2.CODE_MASTER_ID = 350 JOIN SYS0013M T3 ON T3.CODE_ID = T1.APP_TYPE_ID AND T3.CODE_MASTER_ID = 10 JOIN SYS0026M T4 ON T4.STK_ID = T1.ITM_STK_ID AND GET_HOMECARE_FLAG_YN(t4.stk_ctgry_id) = 0
JOIN SAL0029D T5 ON T5.CUST_ID = T1.CUST_ID LEFT JOIN SYS0013M T6 ON T6.CODE_ID = T5.TYPE_ID AND T6.CODE_MASTER_ID = 8 LEFT JOIN SYS0047M T7 ON T7.USER_ID = T1.CRT_USER_ID LEFT JOIN SYS0038M T8 ON T8.STUS_CODE_ID = T1.STUS_ID LEFT JOIN ORG0001D T16 ON T16.MEM_CODE = T1.MEM_CODE LEFT JOIN ORG1001V T9 ON T9.MEM_ID = T16.MEM_ID LEFT JOIN SYS0047M T10 ON T10.USER_ID = T1.UPD_USER_ID LEFT JOIN SAL0001D T11 ON T1.SALES_ORD_ID = T11.SALES_ORD_ID LEFT JOIN SAL0023D T12 ON T12.CUST_ADD_ID = T1.INST_ADD_ID LEFT JOIN SYS0064M T13 ON T13.AREA_ID = T12.AREA_ID LEFT JOIN SAL0017D T14 ON T14.PROMO_ID = T1.PROMO_ID LEFT JOIN ORG0010D DOC ON DOC.DOC_SO_ID = T11.SALES_ORD_ID AND DOC.DOC_TYPE_ID = 3198 AND DOC.STUS_ID = 1 LEFT JOIN SYS0005M BRNCH ON DOC.DOC_SUB_BRNCH_ID = BRNCH.BRNCH_ID LEFT JOIN SYS0005M T17 ON T17.BRNCH_ID = T16.BRNCH LEFT JOIN SYS0013M RGN ON RGN.CODE_ID = T17.REGN_ID
WHERE 1=1
AND T1.MEM_CODE = #{_memCode}
AND T1.APP_TYPE_ID IN
#{item}
AND T1.REQST_DT BETWEEN TO_DATE(#{_reqstStartDt} || ' 00:00:00', 'DD/MM/YYYY HH24:MI:SS') AND TO_DATE(#{_reqstEndDt} || ' 23:59:59', 'DD/MM/YYYY HH24:MI:SS')
AND T1.STUS_ID IN
#{item, jdbcType=VARCHAR}
AND T1.KEYIN_BRNCH_ID IN
#{item}
AND T5.NRIC = #{_nric}
AND T1.SOF_NO = #{_sofNo}
AND T5.TYPE_ID IN
#{item}
AND T5.NAME LIKE #{_name}||'%'
AND T9.ORG_CODE = #{orgCode}
AND T9.GRP_CODE = #{grpCode}
AND T9.DEPT_CODE = #{deptCode}
AND T11.SALES_ORD_NO = #{_ordNo}
AND T1.REQST_DT BETWEEN TO_DATE(#{_reqstStartDt} || #{_reqstStartTime}, 'dd/mm/yyyy hh24:mi') AND TO_DATE(#{_reqstEndDt} || #{_reqstEndTime}, 'dd/mm/yyyy hh24:mi')
AND T4.STK_ID = #{ordProudctList}
AND T7.USER_TYPE_ID = #{memType}
AND T1.CHNNL = #{entryPoint}
ORDER BY T1.PRE_ORD_ID ASC

