SELECT DISTINCT A.ID , B.CODE_NAME AS POS_TYPE , A.SELLING_TYPE AS SELLING_TYPE_ID , C.CODE_NAME AS SELLING_TYPE , E.CODE_NAME AS ITEM_CTGRY , A.ITEM_ID , D.STK_CODE || ' - ' || D.STK_DESC AS STK_CODE , A.ITEM_SIZE , A.ITEM_QTY , A.ITEM_WEIGHT , A.TOTAL_PRICE , A.TOTAL_WEIGHT , A.ITEM_ATTCH_GRP_ID , G.ATCH_FILE_NAME , G.FILE_SUB_PATH || '\' || G.PHYSICL_FILE_NAME AS FILEPATH from SAL0321D A LEFT JOIN SYS0013M B ON A.POS_TYPE = B.CODE_ID LEFT JOIN SYS0013M C ON A.SELLING_TYPE = C.CODE_ID LEFT JOIN SYS0026M D ON A.ITEM_ID = D.STK_ID LEFT JOIN SYS0013M E ON A.ITEM_CTGRY_ID = E.CODE_ID LEFT JOIN SYS0070M F ON A.ITEM_ATTCH_GRP_ID = F.ATCH_FILE_GRP_ID LEFT JOIN SYS0071D G ON F.ATCH_FILE_ID = G.ATCH_FILE_ID LEFT JOIN LOG0106M H ON A.ITEM_ID = H.ITEM_CODE WHERE 1=1 AND NVL(A.DISAB,0) = 0
AND A.POS_TYPE = #{posType_editItem}
AND A.SELLING_TYPE = #{sellingType_editItem}
AND A.ID =#{imgId}
AND H.LOC_ID =#{locId}

