mapper.select:
SELECT A.ID , B.CODE_NAME AS REGIONAL , A.REGIONAL_TYPE , A.TOTAL_WEIGHT_FROM , A.TOTAL_WEIGHT_TO , TO_CHAR(A.START_DT,'DD/MM/YYYY') AS START_DT , TO_CHAR(A.END_DT,'DD/MM/YYYY') AS END_DT , A.TOTAL_SHIPPING_FEE , CASE WHEN TO_CHAR(SYSDATE ,'YYYYMMDD') > TO_CHAR(A.END_DT,'YYYYMMDD') THEN 'Expired' ELSE 'Active' END AS STATUS , CASE WHEN TO_CHAR(SYSDATE ,'YYYYMMDD') >= TO_CHAR(A.START_DT,'YYYYMMDD') THEN 'C' ELSE 'F' END AS STATUS2 from SAL0322D A LEFT JOIN SYS0013M B ON A.REGIONAL_TYPE = B.CODE_ID WHERE 1=1 AND NVL(A.DISAB,0) = 0
AND A.REGIONAL_TYPE = #{regional}
AND A.TOTAL_WEIGHT_FROM = #{weightFrom}
AND A.TOTAL_WEIGHT_TO = #{weightTo}
AND A.START_DT = TO_DATE(#{startDt},'DD/MM/YYYY')
AND A.END_DT = TO_DATE(#{endDt},'DD/MM/YYYY')
AND TO_CHAR(SYSDATE ,'YYYYMMDD') <= TO_CHAR(A.END_DT,'YYYYMMDD')
AND TO_CHAR(SYSDATE ,'YYYYMMDD') > TO_CHAR(A.END_DT,'YYYYMMDD')

