mapper.select:
SELECT DISTINCT A.ESN_NO , C.CODE_NAME AS POS_TYPE , D.NAME AS STATUS , TO_CHAR(A.CRT_DT,'DD-MM-YYYY') AS CRT_DT , TO_CHAR(A.UPD_DT,'DD-MM-YYYY') AS UPD_DT , E.USER_NAME AS CRT_BY , F.USER_NAME AS UPD_BY , A.POS_NO , G.CODE_NAME AS COURIER_SVC , A.WAYBILL_NO FROM SAL0325M A JOIN SAL0326D B ON A.ESN_NO = B.ESN_NO LEFT JOIN SYS0013M C ON A.POS_TYPE = C.CODE_ID LEFT JOIN SYS0038M D ON A.ESN_STATUS = D.STUS_CODE_ID LEFT JOIN SYS0047M E ON A.CRT_USER_ID = E.USER_ID LEFT JOIN SYS0047M F ON A.UPD_USER_ID = F.USER_ID LEFT JOIN SYS0013M G ON A.COURIER_SVC = G.CODE_ID LEFT JOIN ORG0001D H ON E.HR_CODE = H.MEM_CODE LEFT JOIN ORG1001V I ON I.MEM_ID = H.MEM_ID WHERE 1=1
AND A.POS_TYPE = #{posType}
AND A.ESN_NO = #{refNo}
AND A.ESN_STATUS IN
#{item}
AND A.CRT_DT >= TO_DATE(#{fromSalesDt}||' 00:00:00', 'DD-MM-YYYY HH24:MI:SS')
AND A.CRT_DT <= TO_DATE(#{toSalesDt} ||' 23:59:59', 'DD-MM-YYYY HH24:MI:SS')
AND UPPER(E.USER_NAME) LIKE UPPER('%' || #{memberId} || '%')
AND I.ORG_CODE = #{orgCode}
AND I.GRP_CODE = #{grpCode}
AND I.DEPT_CODE = #{deptCode}
AND A.ESN_LOC_ID = #{branch}
ORDER BY ESN_NO DESC

