/* [com.coway.trust.biz.sales.productInfoListApi.impl.ProductInfoListApiMapper.selectProductInfoList] 20191113 - KR JAEMAJEM */ SELECT A.STK_CODE AS STK_CODE , A.STK_DESC AS STK_DESC , A.STK_CTGRY_ID AS STK_CTGRY_ID , B.CODE_NAME AS STK_CTGRY_ID_NAME , CASE WHEN A.STK_COMM_AS IS NOT NULL THEN A.STK_COMM_AS ELSE CAST(0 AS FLOAT (53)) END AS STK_COMM_AS , CASE WHEN A.STK_COMM_BS IS NOT NULL THEN A.STK_COMM_BS ELSE CAST(0 AS FLOAT (53)) END AS STK_COMM_BS , CASE WHEN A.STK_COMM_INS IS NOT NULL THEN A.STK_COMM_INS ELSE CAST(0 AS FLOAT (53)) END AS STK_COMM_INS , A.STK_TYPE_ID AS STK_TYPE_ID , CASE WHEN A.STK_TYPE_ID = 61 THEN NVL(C.AMT, 0) ELSE 0 END AS MONTHLY_RENTAL , CASE WHEN A.STK_TYPE_ID = 61 THEN NVL(C.PRC_RPF, 0) ELSE 0 END AS PRC_RPF , CASE WHEN A.STK_TYPE_ID = 61 THEN 0 ELSE NVL(D.PRC_CHRG, 0) END AS NORMAL_PRICE , CASE WHEN A.STK_TYPE_ID = 61 THEN NVL(D.TRADE_IN_PV, 0) ELSE 0 END AS POINT_OF_VALUE , E.IMG_URL AS IMG_URL FROM SYS0026M A -- ASIS_DB : WebDB ASIS_SCHEMA : dbo ASIS_TABLE : InvStock INNER JOIN SYS0013M B ON A.STK_CTGRY_ID = B.CODE_ID LEFT OUTER JOIN LOG0058D C ON C.APP_TYPE_ID = 66 AND C.MEM_PAC_ID = 2 AND A.STK_ID = C.STK_ID LEFT OUTER JOIN LOG0058D D ON D.APP_TYPE_ID = 66 AND D.MEM_PAC_ID = 0 AND A.STK_ID = D.STK_ID LEFT OUTER JOIN SYS0027M E ON A.STK_ID = E.STK_ID AND E.STUS_CODE_ID = 1 WHERE A.STK_TYPE_ID = 61 -- SYS0013M(61-Stock) AND A.STUS_CODE_ID = 1 -- SYS0038M(1-Active)
AND A.STK_CTGRY_ID = #{stkCtgryId}
ORDER BY A.STK_DESC ASC
