mapper.select:
SELECT
PM.PROMO_ID , PM.PROMO_CODE , PM.PROMO_DESC , PT.CODE_NAME APP_TYPE , PD.CTRL_QUOTA , PD.CTRL_FLAG
FROM SYS0026M S JOIN SAL0018D PD ON PD.PROMO_ITM_STK_ID = S.STK_ID AND PD.PROMO_ITM_STUS_ID = 1 JOIN SAL0017D PM ON PM.PROMO_ID = PD.PROMO_ID LEFT JOIN SYS0013M PT ON PT.CODE_ID = PM.PROMO_APP_TYPE_ID
WHERE SYSDATE BETWEEN PROMO_DT_FROM AND PROMO_DT_END AND PM.PROMO_APP_TYPE_ID IN (2284,2285,2286) AND STK_ID = #{stkId}
AND PD.CTRL_FLAG = #{ctrlFlag}
ORDER BY PM.PROMO_APP_TYPE_ID, PM.PROMO_ID

SELECT COUNT(*) COUNT FROM SAL0001D JOIN SAL0002D ON SAL0002D.SALES_ORD_ID = SAL0001D.SALES_ORD_ID WHERE NVL(SAL0001D.PRE_ORD_ID,0) = 0 AND SAL0001D.STUS_CODE_ID <> 8 AND SAL0002D.ITM_STK_ID = #{quota_stkId} AND SAL0001D.SALES_DT BETWEEN TO_DATE( #{quota_startDt} || #{quota_startTm} ,'DD/MM/YYYYHH:MI AM') AND TO_DATE( #{quota_endDt} || #{quota_endTm} ,'DD/MM/YYYYHH:MI AM')
AND SAL0001D.STUS_CODE_ID IN
#{item}

SELECT COUNT(*) COUNT FROM SAL0213M WHERE STUS_ID <> 8 AND ITM_STK_ID = #{quota_stkId} AND REQST_DT BETWEEN TO_DATE( #{quota_startDt} || #{quota_startTm} ,'DD/MM/YYYY HH:MI AM') AND TO_DATE( #{quota_endDt} || #{quota_endTm} ,'DD/MM/YYYY HH:MI AM')
AND STUS_ID IN
#{item}

mapper.update:
UPDATE SYS0026M
SET
UPD_DT = SYSDATE
, UPD_USER_ID = #{userId}
, IS_CTRL = #{modify_ctrl}


    , DISCONTINUE = 1


    , DISCONTINUE = 0



, CTRL_START_DT = TO_DATE(#{modify_startDt} || #{modify_startTm}, 'DD/MM/YYYYHH:MI AM')


, CTRL_END_DT = TO_DATE(#{modify_endDt} || #{modify_endTm}, 'DD/MM/YYYYHH:MI AM')

WHERE STK_ID = #{modify_stkId}

UPDATE SAL0018D
SET
PROMO_ITM_UPD_DT = SYSDATE
, PROMO_ITM_UPD_USER_ID = #{userId}
, CTRL_FLAG = #{ctrlFlag}
, CTRL_QUOTA = #{ctrlQuota}
WHERE PROMO_ID = #{promoId}
AND PROMO_ITM_STK_ID = #{stkId}
