SELECT SAL0017D_PROMO_ID_SEQ.NEXTVAL FROM DUAL

INSERT
  INTO SAL0017D 
     ( PROMO_ID
     , PROMO_MTCH_ID
     , PROMO_CODE
     , PROMO_DESC
     , PROMO_TYPE_ID
     , PROMO_APP_TYPE_ID
     , PROMO_SRV_MEM_PAC_ID
     , PROMO_DT_FROM
     , PROMO_DT_END
     , PROMO_STUS_ID
     , PROMO_UPD_DT
     , PROMO_UPD_USER_ID
     , PROMO_IS_TRIAL_CNVR
     , PROMO_PRC_PRCNT
     , PROMO_CUST_TYPE
     , PROMO_DISC_TYPE
     , PROMO_RPF_DISC_AMT
     , PROMO_DISC_PERIOD_TP
     , PROMO_DISC_PERIOD
     , PROMO_FREESVC_PERIOD_TP
     , PROMO_ADD_DISC_PRC
     , PROMO_ADD_DISC_PV
     , EMP_CHK
     , EX_TRADE)
VALUES
     ( #{promoId}
     , #{promoMtchId}
     , #{promoCode}
     , #{promoDesc}
     , #{promoTypeId}
     , #{promoAppTypeId}
     , #{promoSrvMemPacId}
     , TO_DATE(#{promoDtFrom}, 'YYYY-MM-DD')
     , TO_DATE(#{promoDtEnd}, 'YYYY-MM-DD')
     , #{promoStusId}
     , SYSDATE
     , #{promoUpdUserId}
     , #{promoIsTrialCnvr}
     , #{promoPrcPrcnt}
     , #{promoCustType}
     , #{promoDiscType}
     , #{promoRpfDiscAmt}
     , #{promoDiscPeriodTp}
     , #{promoDiscPeriod}
     , #{promoFreesvcPeriodTp}
     , #{promoAddDiscPrc}
     , #{promoAddDiscPv}
     , #{empChk}
     , #{exTrade})


