mapper.insert:
MERGE INTO SAL0405D T USING (
SELECT #{item.promoId} PROMO_ID, #{item.promoReqstId} PROMO_REQST_ID, #{item.typeId} TYPE_ID, #{item.itmValue} ITM_VALUE, #{item.stusId} STUS_ID, #{item.userId} CRT_USER_ID, SYSDATE CRT_DT, #{item.userId} UPD_USER_ID, SYSDATE UPD_DT FROM DUAL
)S ON (S.PROMO_ID = T.PROMO_ID AND T.TYPE_ID = S.TYPE_ID AND T.ITM_VALUE = S.ITM_VALUE) WHEN NOT MATCHED THEN INSERT ( PROMO_ID, PROMO_REQST_ID, TYPE_ID, ITM_VALUE, STUS_ID, CRT_USER_ID, CRT_DT )VALUES( S.PROMO_ID, S.PROMO_REQST_ID, S.TYPE_ID, S.ITM_VALUE, S.STUS_ID, S.CRT_USER_ID, S.CRT_DT ) WHEN MATCHED THEN UPDATE SET T.PROMO_REQST_ID = S.PROMO_REQST_ID, T.STUS_ID = S.STUS_ID, T.UPD_USER_ID = S.UPD_USER_ID, T.UPD_DT = S.UPD_DT

MERGE INTO SAL0406D T USING (
SELECT #{item.promoId} PROMO_ID, #{item.promoReqstId} PROMO_REQST_ID, #{item.typeId} TYPE_ID, #{item.itmValue} ITM_VALUE, #{item.stusId} STUS_ID, #{item.userId} CRT_USER_ID, SYSDATE CRT_DT, #{item.userId} UPD_USER_ID, SYSDATE UPD_DT FROM DUAL
)S ON (S.PROMO_REQST_ID = T.PROMO_REQST_ID AND T.TYPE_ID = S.TYPE_ID AND T.ITM_VALUE = S.ITM_VALUE) WHEN NOT MATCHED THEN INSERT ( PROMO_ID, PROMO_REQST_ID, TYPE_ID, ITM_VALUE, STUS_ID, CRT_USER_ID, CRT_DT )VALUES( S.PROMO_ID, S.PROMO_REQST_ID, S.TYPE_ID, S.ITM_VALUE, S.STUS_ID, S.CRT_USER_ID, S.CRT_DT ) WHEN MATCHED THEN UPDATE SET T.PROMO_ID = S.PROMO_ID, T.STUS_ID = S.STUS_ID, T.UPD_USER_ID = S.UPD_USER_ID, T.UPD_DT = S.UPD_DT
