mapper.select:
SELECT Extent1.DEALER_ID DEALER_ID , Extent1.DEALER_NAME DEALER_NAME , Extent1.DEALER_STUS_ID DEALER_STUS_ID , Extent1.DEALER_TYPE , Extent3.CNT_NAME CNT_NAME , Extent3.TEL_M1 TEL_M1 , Extent6.CODE CODE2 FROM SAL0030D Extent1 JOIN SAL0031D Extent2 ON ( Extent2.DEALER_ID = Extent1.DEALER_ID ) AND 9 = Extent2.STUS_CODE_ID JOIN SAL0032D Extent3 ON ( Extent3.DEALER_ID = Extent1.DEALER_ID ) AND 9 = Extent3.STUS_CODE_ID JOIN SYS0038M Extent6 ON Extent6.STUS_CODE_ID = Extent1.DEALER_STUS_ID WHERE 1=1
AND Extent1.DEALER_STUS_ID IN
#{item}
AND Extent1.DEALER_TYPE IN
#{item}
AND Extent1.PST_DEALER_ID = #{pstDealerId}
AND UPPER(Extent1.DEALER_NAME) LIKE '%'||UPPER(#{pstDealerName})||'%'
AND UPPER(Extent3.CNT_NAME) LIKE '%'|| UPPER(#{pstCntName})||'%'
ORDER BY Extent1.DEALER_NAME ASC

        SELECT count(0) CNT
		  FROM SAL0030D
		WHERE DEALER_NRIC = #{dealerNric}


          SELECT 
              AREA_ID
          FROM 
              SYS0064M 
          WHERE
              COUNTRY =  #{countryValue}
          AND
              STATE = #{statValue}
          AND
              CITY = #{cityValue}
          AND
              POSTCODE = #{postCodeValue}
          AND
              AREA = #{areaValue}

