SELECT A.AGENT_ID , A.AGENT_GRP_ID ,B.CODE || '_'||A.AGENT_GRP_ID||'_'||A.AGENT_NAME AGENT_NAME FROM SAL0148M A JOIN SYS0013M B ON A.AGENT_TYPE = B.CODE_ID AND B.CODE_MASTER_ID = 329 AND DISAB = 0 WHERE 1=1
AND A.STUS_ID = 1
AND A.USER_ID= #{userName}
AND A.AGENT_TYPE = #{agentType}
