WITH FIRST_INSTALL AS (SELECT SALES_ORD_ID, INSTALL_DT FROM (SELECT SALES_ORD_ID, INSTALL_DT, RANK () OVER (PARTITION BY SALES_ORD_ID ORDER BY INSTALL_ENTRY_ID ASC) RANK FROM SAL0046D WHERE STUS_CODE_ID = 4) WHERE RANK = 1) SELECT C.SALES_ORD_NO ,A.SALES_ORD_ID ,A.CUST_ID ,B.NAME ,CASE WHEN B.CORP_TYPE_ID = 0 THEN null ELSE H.CODE_NAME END CORP_TYPE_ID ,A.COLCT_TRGET
,(SELECT SUM(RENT_AMT) AS CURRENT_OS FROM PAY0022D WHERE RENT_SO_ID = C.SALES_ORD_ID GROUP BY RENT_SO_ID) RENT_AMT
,(SELECT SUM(TRADE_AMT) AS CURRENT_OS FROM PAY0035D WHERE RENT_SO_ID = C.SALES_ORD_ID GROUP BY RENT_SO_ID) RENT_AMT
,A.OPEN_MTH_AGING ,CASE WHEN A.AGENT_GRP_ID = 0 THEN null ELSE C.AGENT_NAME END AGENT_GRP_ID ,CASE WHEN A.PREV_AGENT_GRP_ID = 0 THEN null ELSE D.AGENT_NAME END ORI_PREV_AGENT_GRP_ID ,CASE WHEN A.AGENT_GRP_ID = 0 THEN null ELSE A.AGENT_GRP_ID END PREV_AGENT_GRP_ID ,CASE WHEN A.AGENT_GRP_ID = 0 THEN (SELECT NVL(AGENT_GRP_ID, 0) FROM SAL0149D WHERE CUST_ID = A.CUST_ID AND AGENT_GRP_ID <> 0 AND ROWNUM =1) ELSE A.AGENT_GRP_ID END SUGGEST_AGENT ,CASE WHEN A.AGENT_GRP_ID = 0 THEN 'N' ELSE 'Y' END ASSIGNED ,CASE WHEN A.SENSITIVE_FG = 0 THEN 'N' ELSE 'Y' END SENSITIVE_FG ,CASE WHEN A.ETR_FG = 0 or A.ETR_FG is null THEN 'N' ELSE 'Y' END ETR_FG ,E.STUS_CODE_ID CURR_RENTAL_STUS ,C.STUS_CODE_ID ,B.TYPE_ID ,TO_CHAR(A.UPD_AGENT_DT, 'dd/mm/yyyy') UPD_AGENT_DT ,A.REM ,A.ETR_REM ,F.CODE_DESC RACE ,cD.UN_BILL_AMT ,(C.MTH_RENT_AMT - NVL(REBATE_AMT_PER_INSTLMT,0)) MTH_RENT_AMT FROM SAL0149D A JOIN SAL0029D B ON A.CUST_ID = B.CUST_ID JOIN SAL0001D C ON A.SALES_ORD_ID = C.SALES_ORD_ID LEFT JOIN ( SELECT A.AGENT_GRP_ID ,B.CODE || '_'||A.AGENT_GRP_ID||'_'||A.AGENT_NAME AGENT_NAME FROM SAL0148M A JOIN SYS0013M B ON A.AGENT_TYPE = B.CODE_ID AND B.CODE_MASTER_ID = 329 AND DISAB = 0 ) C ON A.AGENT_GRP_ID = C.AGENT_GRP_ID LEFT JOIN ( SELECT A.AGENT_GRP_ID ,B.CODE || '_'||A.AGENT_GRP_ID||'_'||A.AGENT_NAME AGENT_NAME FROM SAL0148M A JOIN SYS0013M B ON A.AGENT_TYPE = B.CODE_ID AND B.CODE_MASTER_ID = 329 AND DISAB = 0 ) D ON A.PREV_AGENT_GRP_ID = D.AGENT_GRP_ID LEFT JOIN SAL0071D E ON A.SALES_ORD_ID = E.SALES_ORD_ID LEFT JOIN SYS0013M F ON F.CODE_ID = B.RACE_ID LEFT JOIN FIRST_INSTALL G ON A.SALES_ORD_ID = G.SALES_ORD_ID JOIN REP0010S cD ON a.SALES_ORD_ID = cD.ORD_ID AND cd.SRV_CNTRCT_ID = 0 LEFT JOIN PAY0286D gstRb ON C.SALES_ORD_ID = gstRb.ORD_ID AND gstRb.CNTRCT_ID = 0 AND gstrb.stus_id = 1 LEFT JOIN SYS0013M H ON H.CODE_ID = B.CORP_TYPE_ID LEFT JOIN SVC0008D I ON I.SALES_ORD_ID = A.SALES_ORD_ID AND to_char( sysdate, 'mm' ) = I.Month AND to_char( sysdate, 'yyyy' ) = I.YEAR LEFT JOIN PAY1009V J ON J.ORD_ID = A.SALES_ORD_ID LEFT JOIN SAL0002D K ON K.SALES_ORD_ID = A.SALES_ORD_ID LEFT JOIN SYS0026M L ON L.STK_ID = K.ITM_STK_ID LEFT JOIN SYS0013M M ON M.CODE_ID = L.STK_CTGRY_ID
WHERE 1=1
AND C.SALES_ORD_NO = #{orderNo}
AND A.CUST_ID = #{customerId}
AND A.AGENT_GRP_ID IN
#{item}
AND B.NRIC = #{nric}
AND UPPER(B.NAME) LIKE '%'||#{custName}||'%'
AND B.CUST_VA_NO = #{vaNo}
AND M.CODE_ID IN
#{item}
AND I.MONTH IS NOT NULL
AND I.MONTH IS NULL
AND J.PAY_MODE_ID IN
#{item}
AND A.CUST_ID = #{customerId}
AND A.AGENT_GRP_ID IN
#{item}
AND B.CUST_VA_NO = #{vaNo}
AND B.NRIC = #{nric}
AND A.AGENT_GRP_ID IN
#{item}
AND UPPER(B.NAME) LIKE '%'||#{custName}||'%'
AND A.AGENT_GRP_ID IN
