SELECT EXTENT1.RC_BATCH_ID , EXTENT1.RC_BATCH_NO , EXTENT2.NAME RC_STUS, EXTENT3.CODE_NAME RC_TYPE, EXTENT7.CODE_NAME AGENT_TYPE_UPL, EXTENT6.TOTAL_ITEM, EXTENT6.VALID_ITEM, EXTENT6.INVALID_ITEM, EXTENT4.USER_NAME RC_CRT_USER_NAME, nvl(TO_CHAR(extent1.RC_CRT_DT,'DD/MM/YYYY hh:mi:ss A.M.'),'01-01-1900 AM 00:00:00') AS RC_CRT_DT, EXTENT5.USER_NAME RC_UPD_USER_NAME, CASE WHEN EXTENT1.RC_STUS_ID = 4 THEN nvl(TO_CHAR(extent1.RC_UPD_DT ,'DD/MM/YYYY hh:mi:ss A.M.'),'01-01-1900 AM 00:00:00') ELSE ' - ' END AS RC_UPD_DT FROM SAL0239D EXTENT1 JOIN (SELECT RC_BATCH_ID, COUNT(RC_BATCH_ID) TOTAL_ITEM, COUNT(CASE WHEN RC_ITM_CNVR_STUS_ID = 4 then 1 ELSE NULL END) as VALID_ITEM, COUNT(CASE WHEN RC_ITM_CNVR_STUS_ID = 21 then 1 ELSE NULL END) as INVALID_ITEM FROM SAL0240D GROUP BY RC_BATCH_ID) EXTENT6 ON EXTENT1.RC_BATCH_ID = EXTENT6.RC_BATCH_ID LEFT JOIN SYS0038M EXTENT2 ON EXTENT1.RC_STUS_ID = EXTENT2.STUS_CODE_ID LEFT JOIN SYS0013M EXTENT3 ON EXTENT1.RC_TYPE_ID = EXTENT3.CODE_ID LEFT JOIN SYS0047M EXTENT4 ON EXTENT1.RC_CRT_USER_ID = EXTENT4.USER_ID LEFT JOIN SYS0047M EXTENT5 ON EXTENT1.RC_UPD_USER_ID = EXTENT5.USER_ID LEFT JOIN SYS0013M EXTENT7 ON EXTENT1.AGENT_TYP_UPL_ID = EXTENT7.CODE_ID WHERE 1 = 1
AND EXTENT1.RC_BATCH_NO = #{batchId}
AND EXTENT1.RC_TYPE_ID = #{rcmsUploadType}
AND RC_STUS_ID IN
#{item}
AND EXTENT4.USER_NAME = #{crtUserName}
AND Extent1.RC_CRT_DT >= TO_DATE(TO_CHAR(TO_DATE(#{createStDate},'DD/MM/YYYY'),'YYYYMMDD') || '000000','yyyymmddhh24miss') AND Extent1.RC_CRT_DT < TO_DATE(TO_CHAR(TO_DATE(#{createEnDate},'DD/MM/YYYY'),'YYYYMMDD') || '000000','yyyymmddhh24miss') + 1
ORDER BY EXTENT1.RC_BATCH_ID DESC

SELECT EXTENT2.SALES_ORD_NO , CASE WHEN EXTENT1.RC_ITM_STUS_ID = 4 THEN 'VALID' ELSE 'INVALID' END RC_ITM_STUS, EXTENT1.RC_ITM_FIELD , EXTENT1.RC_ITM_REM , EXTENT3.NAME STUS_NAME, EXTENT1.RC_ITM_REN_STUS, EXTENT1.RC_ITM_AGENT_ID, EXTENT1.RC_ITM_AGENT_GRP_ID, EXTENT1.RC_ITM_CNVR_STUS_ID, EXTENT1.RC_ITM_CNVR_REM FROM SAL0240D EXTENT1 LEFT JOIN SAL0001D EXTENT2 ON EXTENT1.RC_ITM_ORD_ID = EXTENT2.SALES_ORD_ID LEFT JOIN SYS0038M EXTENT3 ON EXTENT1.RC_ITM_CNVR_STUS_ID = EXTENT3.STUS_CODE_ID WHERE 1 = 1
AND EXTENT1.RC_BATCH_ID = #{batchId}
AND EXTENT1.RC_ITM_CNVR_STUS_ID = #{stusId}
ORDER BY RC_ITM_ID ASC

