mapper.select:
SELECT A.TRNSIT_ID , A.TRNSIT_NO , TO_CHAR(A.TRNSIT_DT, 'dd/mm/yyyy') TRNSIT_DT , A.TRNSIT_FROM , A.TRNSIT_TO , A.TRNSIT_CURIER , A.TRNSIT_STUS_ID , A.TRNSIT_STUS_CODE , A.TRNSIT_STUS_NAME , A.TRNSIT_CLOS_DT , A.TRNSIT_CRT_USER_ID , A.TRNSIT_TOT_BOOK FROM MSC1002V A
JOIN ( SELECT B.TR_TRNSIT_ID , B.TR_TRNSIT_STUS_ID , C.TR_BOOK_NO FROM MSC0038D B JOIN MSC0028D C ON B.TR_TRNSIT_BOOK_ID = C.TR_BOOK_ID ) Join1 ON A.TRNSIT_ID = Join1.TR_TRNSIT_ID
WHERE 1=1
AND A.TRNSIT_NO = #{trnsitNo}
AND TO_CHAR(A.TRNSIT_DT, 'dd-mm-yyyy') = #{trnsitDt}
AND A.TRNSIT_CRT_USER_ID = #{crtuserId}
AND A.TRNSIT_STUS_ID = #{trnsitStusId}
AND A.TRNSIT_STUS_ID IN (#{trnsitStusId}, '44' )
AND A.TRNSIT_TO = #{trnsitTo}
AND 1 = Join1.TR_TRNSIT_STUS_ID AND Join1.TR_BOOK_NO = #{trBookNo}
ORDER BY TRNSIT_NO ASC

mapper.update:

        UPDATE MSC0038D
             SET TR_TRNSIT_RESULT_STUS_ID = #{status},
                 TR_CLOS_DT = SYSDATE,
                 TR_TRNSIT_UPD_DT = SYSDATE,
                 TR_TRNSIT_UPD_USER_ID = #{userId}
           WHERE TR_TRNSIT_DET_ID = #{trTrnsitDetId}


      UPDATE MSC0037D
         SET TR_TRNSIT_UPD_DT = SYSDATE,
             TR_TRNSIT_UPD_USER_ID = #{userId},
             TR_TRNSIT_STUS_ID = #{trTrnsitStusId}
       WHERE  TR_TRNSIT_ID = #{trnsitId}
