mapper.select:
SELECT ROWNUM rnum, BOX_ID boxid, BOX_NO boxno, BOX_STUS_ID statusid, BOX_STUS_CODE statuscd, BOX_STUS_NAME status, BOX_HOLDER holder, BOX_OPEN_DT opendt, BOX_SCRAP_DT scrapdt, to_char(BOX_OPEN_DT, 'dd/mm/yyyy') opendt2, to_char(BOX_SCRAP_DT, 'dd/mm/yyyy') scrapdt2, BOX_CRT_BRNCH_ID branchid, BOX_CRT_BRNCH_CODE branchcd, BOX_CRT_BRNCH_NAME branchnm, BOX_CRT_DT crtdt, to_char(BOX_CRT_DT , 'dd/mm/yyyy') crtdt2, BOX_CRT_USER_ID crtuser, BOX_CRT_USER_NAME crtname, BOX_BOOK_QTY bookqty FROM MSC1003V WHERE 1 = 1
AND BOX_STUS_ID in
#{item}
AND BOX_CRT_DT >= to_date(#{sdate}||'000000' , 'dd/mm/yyyyhh24miss)
AND BOX_CRT_DT <= to_date(#{edate}||'235959' , 'dd/mm/yyyyhh24miss)
AND (BOX_CRT_USER_ID = #{crtuser} or BOX_CRT_USER_NAME like '%'||#{ctruser}||'%')
AND BOX_CRT_BRNCH_ID = #{branchid}
AND BOX_NO = #{trbxnumber}
AND BOX_ID = #{trboxid}
AND BOX_HOLDER = #{bulkholder}
ORDER BY BOX_ID

SELECT M32.TR_BOX_DET_ID trbdetid , M32.TR_BOOK_ID trbid , M32.TR_BOOK_CRT_DT crtdt , M28.TR_BOOK_NO trbno , M28.TR_RECIPT_NO_STR reciptstr , M28.TR_RECIPT_NO_END reciptend , NVL(S47.USER_NAME , '') crtnm FROM MSC0032D M32 , MSC0028D M28 , SYS0047M S47 WHERE 1 = 1 AND M32.TR_BOOK_CRT_USER_ID = S47.USER_ID(+) AND M32.TR_BOOK_ID = M28.TR_BOOK_ID AND M32.TR_BOX_ITM_STUS_ID IN (1 , 4) AND M32.TR_BOX_ID = #{trboxid}
AND M28.TR_BOOK_NO = #{bookno}
AND M28.TR_RECIPT_NO_STR >= #{receiptfr}
AND M28.TR_RECIPT_NO_END <= #{receiptfr}
order by M28.TR_BOOK_NO

mapper.update:
UPDATE SYS0022C
   set DOC_NO = #{nextdocno}
 WHERE doc_no_id=#{docid}
   AND DOC_NO_PREFIX = #{prefix}

       UPDATE MSC0032D
          set TR_BOX_ITM_STUS_ID = 8
			, TR_BOOK_UPD_DT = sysdate
			, TR_BOOK_UPD_USER_ID = #{userid}
        WHERE TR_BOX_DET_ID=#{trbdetid}

UPDATE MSC0031D
   set TR_BOX_STUS_ID = #{statusid}
     , TR_BOX_UPD_DT = sysdate
     , TR_BOX_UPD_USER_ID = #{userid}
 WHERE TR_BOX_ID=#{trboxid}

UPDATE MSC0031D
   set TR_BOX_SCRAP_DT_TM = to_date(#{scrapdt},'dd/mm/yyyy')
     , TR_BOX_UPD_DT = sysdate
     , TR_BOX_UPD_USER_ID = #{userid}
 WHERE TR_BOX_ID=#{trboxid}

mapper.select:
SELECT TRNSIT_ID trnsitid , TRNSIT_NO trnsitno , TRNSIT_DT trnsitdt , TO_CHAR(TRNSIT_DT , 'DD/MM/YYYY') trnsitdt2 , TRNSIT_FROM trnsitfr , TRNSIT_TO trnsitto , TRNSIT_CURIER trnsitcur , TRNSIT_STUS_ID trnsitstid , TRNSIT_STUS_CODE trnsitstcd , TRNSIT_STUS_NAME trnsitstnm , TRNSIT_CLOS_DT trnsitcldt , TO_CHAR(TRNSIT_CLOS_DT , 'DD/MM/YYYY') trnsitcldt2 , TRNSIT_CRT_USER_ID trnsitcrtid , TRNSIT_TOT_BOX trnsittotbox , TR_TRNSIT_ID trtrnsitid , TR_TRNSIT_STUS_ID trtrnsitstid , TR_BOX_NO trboxno FROM MSC1004V MV , (SELECT M35.TR_TRNSIT_ID , M35.TR_TRNSIT_STUS_ID , M31.TR_BOX_NO FROM MSC0035D M35 , MSC0031D M31 WHERE 1 = 1 AND M35.TR_TRNSIT_BOX_ID = M31.TR_BOX_ID) JT WHERE MV.TRNSIT_ID = JT.TR_TRNSIT_ID AND JT.TR_TRNSIT_STUS_ID = 1
AND MV.TRNSIT_NO = #{trnsitno}
AND MV.TRNSIT_DT BETWEEN TO_DATE(#{crtdt}||'000000' , 'YYYYMMDDHH24MISS') AND TO_DATE(#{crtdt}||'235959' , 'YYYYMMDDHH24MISS')
AND MV.TRNSIT_STUS_ID IN ( 1 , 44)
AND MV.TRNSIT_STUS_ID = #{status}
AND MV.TRNSIT_CRT_USER_ID = ${crtuser}
AND MV.TRNSIT_TO = #{branchid}
AND JT.TR_BOX_NO = #{boxno}
ORDER BY TRNSIT_NO ASC

SELECT T1.TR_TRNSIT_DET_ID ttdi , T1.TR_TRNSIT_RESULT_STUS_ID ttrsi , T1.TR_BOX_ID tbi , T1.TR_BOX_NO tbn , T1.CODE code , COUNT(1) CNT FROM ( SELECT M35.TR_TRNSIT_DET_ID , M35.TR_TRNSIT_ID , M35.TR_TRNSIT_RESULT_STUS_ID , M31.TR_BOX_ID , M31.TR_BOX_NO , S38.CODE FROM MSC0035D M35 , MSC0031D M31 , SYS0038M S38 WHERE 1 = 1 AND M35.TR_TRNSIT_BOX_ID = M31.TR_BOX_ID AND M35.TR_TRNSIT_RESULT_STUS_ID = S38.STUS_CODE_ID AND M35.TR_TRNSIT_STUS_ID <> 8 ) T1 , (SELECT * FROM MSC0032D WHERE TR_BOX_ITM_STUS_ID = 1)M32 WHERE 1 = 1 AND T1.TR_BOX_ID = M32.TR_BOX_ID(+) AND T1.TR_TRNSIT_ID = #{trnsitid}
AND T1.TR_TRNSIT_RESULT_STUS_ID = #{statuschk}
GROUP BY T1.TR_TRNSIT_DET_ID , T1.TR_TRNSIT_RESULT_STUS_ID , T1.TR_BOX_ID , T1.TR_BOX_NO , T1.CODE

mapper.update:
        update MSC0034D
		   set TR_TRNSIT_STUS_ID = #{status}
		     , TR_TRNSIT_UPD_DT = sysdate
		     , TR_TRNSIT_UPD_USER_ID = #{userid}

		     , TR_TRNSIT_CLOS_DT = sysdate

		  where TR_TRNSIT_ID = #{trnsitid}

        UPDATE MSC0035D
		   SET TR_TRNSIT_RESULT_STUS_ID  = #{resultstaus}
		     , TR_CLOS_DT                = SYSDATE
		     , TR_TRNSIT_UPD_DT          = SYSDATE
		     , TR_TRNSIT_UPD_USER_ID     = #{userid}
		 WHERE TR_TRNSIT_DET_ID          = #{transitditid}
