mapper.select:
SELECT D.CODE_NAME AS STOCK_TYPE_NAME , C.CODE_NAME AS STOCK_CATEGORY_NAME , A.STOCK_CODE , B.STK_DESC AS STOCK_NAME , A.TOT_AMT , A.TRAN_AMT , A.HAND_AMT , A.DAYS_AMT , A.AGE_AMT , A.STKB_AMT , A.TOT_QTY , A.TRAN_QTY , A.HAND_QTY , A.DAYS_QTY , A.AGE_QTY , A.STKB_QTY , A.MM6_ISS_QTY , A.MM5_ISS_QTY , A.MM4_ISS_QTY , A.MM3_ISS_QTY , A.MM2_ISS_QTY , A.MM1_ISS_QTY , A.M0_PLAN_QTY , A.M1_PLAN_QTY , A.M2_PLAN_QTY , A.AVG_FORW_QTY FROM SCM0057L A LEFT OUTER JOIN SYS0026M B ON (A.STOCK_CODE = B.STK_CODE) LEFT OUTER JOIN SYS0013M C ON (B.STK_CTGRY_ID = C.CODE_ID AND C.CODE_MASTER_ID = 11) /* 11 : Stock Category */ LEFT OUTER JOIN SYS0013M D ON (B.STK_TYPE_ID = D.CODE_ID AND D.CODE_MASTER_ID = 15) /* 15 : Spare Part Type */ WHERE A.YEAR_MONTH = TO_CHAR(ADD_MONTHS(TO_DATE(#{planYearMonth} || '01', 'YYYYMMDD'), -1), 'YYYYMM')
AND D.CODE_ID = #{stockTypeId}
ORDER BY C.CODE_NAME, D.CODE_NAME, A.STOCK_CODE
