mapper.select:
SELECT GRP ,STOCK_CTGRY ,M1_BIZ_PLAN ,M1_SALES_PLAN ,M1_ORDED ,M1_RATE ,M2_BIZ_PLAN ,M2_SALES_PLAN ,M2_ORDED ,M2_RATE ,M3_BIZ_PLAN ,M3_SALES_PLAN ,M3_ORDED ,M3_RATE ,M4_BIZ_PLAN ,M4_SALES_PLAN ,M4_ORDED ,M4_RATE
,M5_BIZ_PLAN ,M5_SALES_PLAN ,M5_ORDED ,M5_RATE ,M6_BIZ_PLAN ,M6_SALES_PLAN ,M6_ORDED ,M6_RATE ,M7_BIZ_PLAN ,M7_SALES_PLAN ,M7_ORDED ,M7_RATE ,M8_BIZ_PLAN ,M8_SALES_PLAN ,M8_ORDED ,M8_RATE
,M9_BIZ_PLAN ,M9_SALES_PLAN ,M9_ORDED ,M9_RATE ,M10_BIZ_PLAN ,M10_SALES_PLAN ,M10_ORDED ,M10_RATE ,M11_BIZ_PLAN ,M11_SALES_PLAN ,M11_ORDED ,M11_RATE ,M12_BIZ_PLAN ,M12_SALES_PLAN ,M12_ORDED ,M12_RATE FROM ( WITH BODY_CONT AS ( SELECT GRP ,STOCK_CTGRY ,SCM_MONTH ,BIZ_PLAN ,SALES_PLAN ,ORDED ,ROUND(RATE,1) RATE FROM SCM0062V WHERE SCM_YEAR = #{scmYearCbBox}
AND STOCK_CTGRY = #{paramStockCtgry}
AND GRP= #{paramGrp}
GROUP BY GRP ,SCM_YEAR ,SCM_QUARTER ,SCM_MONTH ,STOCK_CTGRY ,BIZ_PLAN ,SALES_PLAN ,ORDED ,RATE ) SELECT * FROM BODY_CONT PIVOT ( MAX(BIZ_PLAN) AS BIZ_PLAN , MAX(SALES_PLAN) AS SALES_PLAN , MAX(ORDED) AS ORDED , MAX(RATE ) AS RATE FOR SCM_MONTH IN (1 AS M1,2 AS M2,3 AS M3,4 AS M4,5 AS M5,6 AS M6,7 AS M7,8 AS M8,9 AS M9,10 AS M10,11 AS M11,12 AS M12) ) ) SCM62V ORDER BY GRP ,STOCK_CTGRY
