mapper.select:
	SELECT DISTINCT 
	      PLAN_ID CODE_VALUE 
	    , CDC     CODE_VIEW
  FROM SCM0005M 
 WHERE PLAN_YEAR  = #{planYear}  

   AND PLAN_MONTH = #{planMonth}  

   AND PLAN_WEEK  = #{planWeek}  
   AND PLAN_STUS  = 4
 ORDER BY PLAN_ID

