mapper.select:
SELECT PO_NO ,TO_CHAR(TO_DATE(PO_ISSUE_DT,'YYYY/MM/DD'),'DD/MM/YYYY') PO_ISSUE_DT ,STOCK_CODE ,STOCK_DESC ,STOCK_TYPE ,TO_CHAR(TO_DATE(GR_DT,'YYYY/MM/DD'),'DD/MM/YYYY') GR_DT ,PO_QTY ,DECODE(PO_STUS,'1','Approved','Active') PO_STUS
/*SO*/ ,SO_QTY ,TO_CHAR(TO_DATE(SO_DT,'YYYY/MM/DD'),'DD/MM/YYYY') SO_DT
/*PP*/ ,PP_PLAN_QTY ,PP_PROD_QTY ,PP_PROD_START_DT ,PP_PROD_END_DT
/*GI*/ ,GI_QTY ,TO_CHAR(TO_DATE(GI_DT,'YYYY/MM/DD'),'DD/MM/YYYY') GI_DT
/*SBO*/ ,SBO_PO_QTY ,SBO_AP_QTY ,SBO_GR_QTY FROM SCM0039M WHERE 1 = 1
AND PO_ISSUE_DT >= TO_CHAR(TO_DATE(#{startDate},'DD/MM/YYYY'),'YYYYMMDD') AND PO_ISSUE_DT <= TO_CHAR(TO_DATE(#{endDate},'DD/MM/YYYY'),'YYYYMMDD')
AND PO_NO = #{poNoTxt}
AND PO_STUS = #{statusSelBox}
AND STOCK_TYPE = #{scmStockType}

SELECT SPMON , MATNR , MATNR_NM , STOCK_TYPE , STK_TYPE_ID , CTGRY , MEASURE , AMT_K , WAERS_KRW , TO_CHAR(AMT_SUB, 'fm999,999,999,999.00') AMT_SUB , WAERS_SUB , CNT , MEINS , AMT_USD , WAERS_USD FROM SCM0045V WHERE SPMON = #{targetDateCbBox}
AND STK_TYPE_ID = #{scmStockType}
ORDER BY MATNR DESC

SELECT 'PDF'||'/ EXCEL' PDF ,ROWNUM NO ,PO_NO ,PO_ITM_NO ,EST_WEEK ,GR_WEEK ,TO_CHAR(PO_ISSU_DT,'dd-mm-YYYY') PO_ISSU_DT ,STOCK_CODE ,STK_DESC STOCK_NAME ,QTY ,TO_CHAR(FOB_AMT, 'fm999,999,999,999.00') FOB_AMT ,CDC ,RTPGR_WEEK ,PO_APPV_STUS
,PO_ID ,BAS_YEAR ,PO_STUS ,CRT_DT ,CRT_USER_ID ,UPD_DT ,UPD_USER_ID ,IF_DT ,GR_YEAR ,GR_MONTH ,SN ,STOCK_CTGRY ,STOCK_ID ,STK_TYPE_ID ,PO_ITM_APPV_STUS ,GR_DT FROM SCM0041V WHERE BAS_YEAR = #{scmYearCbBox} AND EST_WEEK = #{scmPeriodCbBox} AND CDC = #{cdcCbBox}
AND STOCK_CODE = #{stockCodeCbBox}
AND STK_TYPE_ID = #{scmStockType}
ORDER BY PO_NO DESC

SELECT 0 checkFlag , PRE_YEAR , PRE_MONTH , PRE_WEEK_TH , PRE_STOCK_CODE STOCK_CODE , STK_DESC STOCK_NAME , STK_TYPE_ID , PRE_QTY PLAN_QTY , NVL(PO_QTY,0) PO_QTY , PRE_CDC , PRE_CRT_DT , PRE_PO_ITM_STUS , PRE_PO_ITM_STUS_DT , NVL(UNIT_PRC,0) FOB_PRICE , FOB_AMT FOB_AMOUNT FROM SCM0042V WHERE PRE_YEAR = #{scmYearCbBox} AND PRE_WEEK_TH = #{scmPeriodCbBox} AND PRE_CDC = #{cdcCbBox}
AND PRE_STOCK_CODE = #{stockCodeCbBox}
AND STK_TYPE_ID = #{scmStockType}

          SELECT  
				        (
				          SELECT COUNT(1) 
				            FROM SCM0011D
				           WHERE PRE_YEAR    = #{scmYearCbBox}  
				             AND PRE_WEEK_TH = #{scmPeriodCbBox}  
				             AND PRE_CDC     = #{cdcCbBox}
				             AND (PRE_QTY - PO_QTY)  >  0
				        ) PRE_POITEM_CNT
				       ,(
				           SELECT COUNT(1) 
				             FROM SCM0009M 
				            WHERE BAS_YEAR = #{scmYearCbBox}  
				              AND EST_WEEK = #{scmPeriodCbBox}   
				              AND CDC      = #{cdcCbBox}
				              AND PO_STUS  <>  5 
				        )  SCMPOMASTER_CNT
				   FROM DUAL

mapper.update:

UPDATE SCM0011D
   SET PO_QTY      = #{planQty}
 WHERE PRE_YEAR    = #{preYear}
   AND PRE_MONTH   = #{preMonth}
   AND PRE_WEEK_TH = #{preWeekTh}
   AND PRE_STOCK_CODE = #{stockCode}
   AND PRE_CDC        = #{preCdc}

mapper.select:
WITH PO_APPROVAL_SUMMARY AS ( SELECT NVL(STOCK_CTGRY,'ZZ_SUM') STOCK_CTGRY , NVL(CDC,'ALL_SUM') CDC , SUM(QTY) QTY , SUM(FOB_AMT) FOB_AMT FROM SCM0043V WHERE BAS_YEAR = #{scmYearCbBox} AND EST_WEEK = #{scmPeriodCbBox}
AND STK_TYPE_ID = #{scmStockType}
GROUP BY ROLLUP (STOCK_CTGRY,CDC) ) SELECT STOCK_CTGRY ,JB_QTY ,JB_FOB_AMT ,KC_QTY ,KC_FOB_AMT ,KK_QTY ,KK_FOB_AMT ,KL_QTY ,KL_FOB_AMT ,PN_QTY ,PN_FOB_AMT ,ALL_SUM_QTY ,ALL_SUM_FOB_AMT FROM PO_APPROVAL_SUMMARY PIVOT ( SUM (QTY) QTY ,SUM(FOB_AMT) FOB_AMT FOR CDC IN ('KC' AS KC ,'KK' AS KK ,'KL' AS KL ,'JB' AS JB ,'PN' AS PN ,'ALL_SUM' AS ALL_SUM ) ) WHERE STOCK_CTGRY <> 'ZZ_SUM' ORDER BY STOCK_CTGRY

 SELECT CODE
   FROM SYS0013M 
  WHERE CODE_MASTER_ID = 349
  MINUS
SELECT CDC 
  FROM SCM0043V 
 WHERE BAS_YEAR = #{scmYearCbBox}
   AND EST_WEEK = #{scmPeriodCbBox}   

   AND STK_TYPE_ID = #{scmStockType}

 GROUP BY CDC

SELECT PO_ITM_APPV_STUS , STK_TYPE_ID , PO_NO , PO_ITM_NO , GR_WEEK , TO_CHAR(PO_ISSU_DT,'DD-MM-YYYY') PO_ISSU_DT , STOCK_CTGRY , STOCK_CODE , STK_DESC , QTY , FOB_AMT , CDC , TO_CHAR(IF_DT ,'DD-MM-YYYY') IF_DT
, PRE_SCM_YEAR , PRE_EST_WEEK , PRE_CDC , PRE_STOCK_CODE , PRE_STOCK_DESC , PRE_QTY , PRE_FOB_PRC , PO_ID , BAS_YEAR , EST_WEEK , RTPGR_WEEK , PO_STUS , CRT_DT , CRT_USER_ID , UPD_DT , UPD_USER_ID , SN , STOCK_ID , PO_APPV_STUS FROM SCM0043V WHERE BAS_YEAR = #{scmYearCbBox} AND EST_WEEK = #{scmPeriodCbBox}
AND STK_TYPE_ID = #{scmStockType}
AND PO_NO IS NOT NULL

mapper.update:
UPDATE SCM0010D
   SET PO_ITM_APPV_STUS = 5
 WHERE PO_NO      = #{poNo}    
   AND PO_ITM_NO  = #{poItmNo}
   AND STOCK_CODE = #{stockCode}
