mapper.select:
	SELECT DISTINCT 
	      PLAN_ID CODE_VALUE 
	    , CDC     CODE_VIEW
  FROM SCM0005M 
 WHERE PLAN_YEAR  = #{planYear}  

   AND PLAN_MONTH = #{planMonth}  

   AND PLAN_WEEK  = #{planWeek}  
   AND PLAN_STUS  = 4
 ORDER BY PLAN_ID

SELECT MOD( DENSE_RANK() OVER ( ORDER BY STOCK_CTGRY ,STOCK_NAME),2) DIV_ODD ,PLAN_DTL_ID ,PLAN_MASTER_ID ,PSI_ID ,STOCK_CODE CODE ,CMPLT IS_SAVED ,LAST_UPD_COLUMN ,PSI_NAME PSI ,STOCK_CTGRY CATEGORY ,STOCK_NAME NAME ,M_3 ,M_2 ,M_1 ,M3_ISSU_AVG ,SALES_PLAN_PRCNTAG ,ROUND(M0_ORD) M0_ORD ,ROUND(M0_PLAN) M0_PLAN_ORDER ,ROUND(M1) M1 ,ROUND(M2) M2 ,ROUND(M3) M3 ,ROUND(M4) M4 ,ROUND(OVERDUE) OVERDUE ,ROUND(WS0) BEF_1_WEEK_TH ,ROUND(WS1) BEF_2_WEEK_TH ,ROUND(WS2) BEF_3_WEEK_TH ,ROUND(WS3) BEF_4_WEEK_TH ,ROUND(W00) W00 ,ROUND(W01) W01 ,ROUND(W02) W02 ,ROUND(W03) W03 ,ROUND(W04) W04 ,ROUND(W05) W05 ,ROUND(W06) W06 ,ROUND(W07) W07 ,ROUND(W08) W08 ,ROUND(W09) W09 ,ROUND(W10) W10 ,ROUND(W11) W11 ,ROUND(W12) W12 ,ROUND(W13) W13 ,ROUND(W14) W14 ,ROUND(W15) W15 ,ROUND(W16) W16 ,ROUND(W17) W17 ,ROUND(W18) W18 ,ROUND(W19) W19 ,ROUND(W20) W20 ,ROUND(W21) W21 ,ROUND(W22) W22 ,ROUND(W23) W23 ,ROUND(W24) W24 ,ROUND(W25) W25 ,ROUND(W26) W26 ,ROUND(W27) W27 ,ROUND(W28) W28 ,ROUND(W29) W29 ,ROUND(W30) W30 FROM SCM0006D WHERE PLAN_MASTER_ID = #{cdcCbBox}
AND STOCK_CODE = #{stockCodeCbBox}
ORDER BY STOCK_CTGRY ,STOCK_NAME ,PSI_ID

SELECT MOD( DENSE_RANK() OVER ( ORDER BY STOCK_CTGRY,STOCK_NAME ),2 ) DIV_ODD ,PLAN_YEAR ,PLAN_MONTH ,PLAN_WEEK ,STOCK_CTGRY CATEGORY ,STOCK_CODE CODE ,STOCK_NAME NAME ,PSI_ID ,PSI_NAME PSI ,ROUND(M0_ORD) M0_ORD ,ROUND(M0_PLAN) M0_PLAN_ORDER ,ROUND(M1) M1 ,ROUND(M2) M2 ,ROUND(M3) M3 ,ROUND(M4) M4 ,ROUND(OVERDUE) OVERDUE ,ROUND(WS0) BEF_1_WEEK_TH ,ROUND(WS1) BEF_2_WEEK_TH ,ROUND(WS2) BEF_3_WEEK_TH ,ROUND(WS3) BEF_4_WEEK_TH ,ROUND(W00) W00 ,ROUND(W01) W01 ,ROUND(W02) W02 ,ROUND(W03) W03 ,ROUND(W04) W04 ,ROUND(W05) W05 ,ROUND(W06) W06 ,ROUND(W07) W07 ,ROUND(W08) W08 ,ROUND(W09) W09 ,ROUND(W10) W10 ,ROUND(W11) W11 ,ROUND(W12) W12 ,ROUND(W13) W13 ,ROUND(W14) W14 ,ROUND(W15) W15 ,ROUND(W16) W16 ,ROUND(W17) W17 ,ROUND(W18) W18 ,ROUND(W19) W19 ,ROUND(W20) W20 ,ROUND(W21) W21 ,ROUND(W22) W22 ,ROUND(W23) W23 ,ROUND(W24) W24 ,ROUND(W25) W25 ,ROUND(W26) W26 ,ROUND(W27) W27 ,ROUND(W28) W28 ,ROUND(W29) W29 ,ROUND(W30) W30 FROM SCM0040V WHERE PLAN_YEAR = #{scmYearCbBox} AND PLAN_WEEK = #{scmPeriodCbBox}
AND STOCK_CODE = #{stockCodeCbBox}
ORDER BY STOCK_CTGRY ,STOCK_NAME ,PSI_ID
