mapper.select:
SELECT A.SCM_MONTH AS PLAN_MONTH
  FROM SCM0018M A
 WHERE A.SCM_YEAR = #{scmYearCbBox}
   AND A.WEEK_TH = #{scmWeekCbBox}
 ORDER BY A.SCM_MONTH DESC /* SPLIT 마지막 주차를 조회하는 경우 다음달을 월로 던져주기 위해서 ORDER BY 사용 */

SELECT A.PLAN_ID , A.PLAN_YEAR , CASE WHEN A.PLAN_MONTH = A.SCM_MONTH THEN A.PLAN_MONTH ELSE A.SCM_MONTH END AS PLAN_MONTH , A.PLAN_WEEK , A.TEAM , A.PLAN_STUS_NM , A.CRT_DT , A.STUS_DT , A.CRT_USER_ID , A.PLAN_STUS_ID FROM ( SELECT A.PLAN_ID , A.PLAN_YEAR , A.PLAN_MONTH , (SELECT MAX(Z.SCM_MONTH) FROM SCM0018M Z WHERE A.PLAN_YEAR = Z.SCM_YEAR AND A.PLAN_WEEK = Z.WEEK_TH) AS SCM_MONTH , A.PLAN_WEEK , A.TEAM , DECODE(A.PLAN_STUS_ID, 1, 'Active', 'Confirmed') AS PLAN_STUS_NM , TO_CHAR(A.CRT_DT, 'YYYY/MM/DD HH24:MI:SS') AS CRT_DT , A.STUS_DT , A.CRT_USER_ID , A.PLAN_STUS_ID FROM SCM0001M A WHERE A.PLAN_YEAR = #{scmYearCbBox} AND A.PLAN_WEEK = #{scmWeekCbBox}
AND A.TEAM = #{scmTeamCbBox}
) A ORDER BY A.TEAM

SELECT A.PLAN_ID , B.PLAN_DTL_ID , A.PLAN_STUS_ID , A.TEAM , D.STK_TYPE_ID AS TYPE_ID , E.CODE_NAME AS TYPE_NAME , D.STK_CTGRY_ID AS CATEGORY_ID , F.CODE_NAME AS CATEGORY_NAME , B.STOCK_CODE AS CODE , D.STK_DESC AS NAME , NVL(B.PRE_M3_AVG_ISSU, 0) AS M3_ISSUE_AVG , NVL(B.M1_ORD, 0) AS M1_ISSUE /* , NVL(B.M0_ORD, 0) + CASE WHEN B.M0_PLAN IS NULL OR B.M0_PLAN = 0 THEN GBSLCVD.FN_GET_M_PLAN('M0', A.PLAN_YEAR, A.PLAN_WEEK, A.TEAM, B.STOCK_CODE) ELSE B.M0_PLAN END AS M0_EXP , NVL(B.M0_ORD, 0) AS M0_ORDER , CASE WHEN B.M0_PLAN IS NULL OR B.M0_PLAN = 0 THEN GBSLCVD.FN_GET_M_PLAN('M0', A.PLAN_YEAR, A.PLAN_WEEK, A.TEAM, B.STOCK_CODE) ELSE B.M0_PLAN END AS M0_PLAN , CASE WHEN B.M1 IS NULL OR B.M1 = 0 THEN GBSLCVD.FN_GET_M_PLAN('M1', A.PLAN_YEAR, A.PLAN_WEEK, A.TEAM, B.STOCK_CODE) ELSE B.M1 END AS M1 , CASE WHEN B.M2 IS NULL OR B.M2 = 0 THEN GBSLCVD.FN_GET_M_PLAN('M2', A.PLAN_YEAR, A.PLAN_WEEK, A.TEAM, B.STOCK_CODE) ELSE B.M2 END AS M2 , CASE WHEN B.M3 IS NULL OR B.M3 = 0 THEN GBSLCVD.FN_GET_M_PLAN('M3', A.PLAN_YEAR, A.PLAN_WEEK, A.TEAM, B.STOCK_CODE) ELSE B.M3 END AS M3 , CASE WHEN B.M4 IS NULL OR B.M4 = 0 THEN GBSLCVD.FN_GET_M_PLAN('M4', A.PLAN_YEAR, A.PLAN_WEEK, A.TEAM, B.STOCK_CODE) ELSE B.M4 END AS M4 , GBSLCVD.FN_GET_M_PLAN_CNT('M0', A.PLAN_YEAR, A.PLAN_WEEK, A.TEAM, B.STOCK_CODE) AS M0_PLAN , GBSLCVD.FN_GET_M_PLAN_CNT('M1', A.PLAN_YEAR, A.PLAN_WEEK, A.TEAM, B.STOCK_CODE) AS M1 , GBSLCVD.FN_GET_M_PLAN_CNT('M2', A.PLAN_YEAR, A.PLAN_WEEK, A.TEAM, B.STOCK_CODE) AS M2 , GBSLCVD.FN_GET_M_PLAN_CNT('M3', A.PLAN_YEAR, A.PLAN_WEEK, A.TEAM, B.STOCK_CODE) AS M3 , GBSLCVD.FN_GET_M_PLAN_CNT('M4', A.PLAN_YEAR, A.PLAN_WEEK, A.TEAM, B.STOCK_CODE) AS M4*/ , NVL(B.M0_ORD, 0) AS M0_ORDER , NVL(B.M0_PLAN, 0) AS M0_PLAN , NVL(B.M0_ORD, 0) + NVL(B.M0_PLAN, 0) AS M0_EXP , NVL(B.M1, 0) AS M1 , NVL(B.M2, 0) AS M2 , NVL(B.M3, 0) AS M3 , NVL(B.M4, 0) AS M4 , NVL(B.W01, 0) AS W01 , NVL(B.W02, 0) AS W02 , NVL(B.W03, 0) AS W03 , NVL(B.W04, 0) AS W04 , NVL(B.W05, 0) AS W05 , NVL(B.W06, 0) AS W06 , NVL(B.W07, 0) AS W07 , NVL(B.W08, 0) AS W08 , NVL(B.W09, 0) AS W09 , NVL(B.W10, 0) AS W10 , NVL(B.W11, 0) AS W11 , NVL(B.W12, 0) AS W12 , NVL(B.W13, 0) AS W13 , NVL(B.W14, 0) AS W14 , NVL(B.W15, 0) AS W15 , NVL(B.W16, 0) AS W16 , NVL(B.W17, 0) AS W17 , NVL(B.W18, 0) AS W18 , NVL(B.W19, 0) AS W19 , NVL(B.W20, 0) AS W20 , NVL(B.W21, 0) AS W21 , NVL(B.W22, 0) AS W22 , NVL(B.W23, 0) AS W23 , NVL(B.W24, 0) AS W24 , NVL(B.W25, 0) AS W25 , NVL(B.W26, 0) AS W26 , NVL(B.W27, 0) AS W27 , NVL(B.W28, 0) AS W28 , NVL(B.W29, 0) AS W29 , NVL(B.W30, 0) AS W30 , NVL(B.WS1, 0) AS WS1 , NVL(B.WS2, 0) AS WS2 , NVL(B.WS3, 0) AS WS3 , NVL(B.WS4, 0) AS WS4 , NVL(B.WS5, 0) AS WS5 FROM SCM0001M A LEFT OUTER JOIN SCM0002D B ON (A.PLAN_ID = B.PLAN_MASTER_ID) LEFT OUTER JOIN SCM0008M C ON (B.STOCK_CODE = C.STOCK_CODE) LEFT OUTER JOIN SYS0026M D ON (C.STOCK_ID = D.STK_ID) LEFT OUTER JOIN SYS0013M E ON (D.STK_CTGRY_ID = E.CODE_ID AND E.CODE_MASTER_ID = 11) /* 11 : Stock Category */ LEFT OUTER JOIN SYS0013M F ON (D.STK_TYPE_ID = F.CODE_ID AND F.CODE_MASTER_ID = 15) /* 15 : Spare Part Type */ WHERE A.PLAN_YEAR = #{scmYearCbBox} AND A.PLAN_WEEK = #{scmWeekCbBox}
AND A.TEAM = #{scmTeamCbBox}
AND D.STK_CTGRY_ID IN
#{item}
AND D.STK_TYPE_ID IN
#{item}
AND ((B.STOCK_CODE LIKE '%' || #{scmStockCode} || '%') OR (D.STK_DESC LIKE '%' || #{scmStockCode} || '%')) ORDER BY A.PLAN_ID DESC, B.STOCK_CODE

mapper.insert:
INSERT INTO SCM0001M
(
       PLAN_ID
     , TEAM
     , PLAN_YEAR
     , PLAN_MONTH
     , PLAN_WEEK
     , PLAN_STUS_ID
     , CRT_DT
     , CRT_USER_ID
 )
VALUES
(
       SCM0001M_PLAN_ID_SEQ.NEXTVAL
     , #{scmTeamCbBox}
     , #{scmYearCbBox}
     , #{planMonth}
     , #{scmWeekCbBox}
     , #{planStusId}
     , SYSDATE
     , #{crtUserId}
 )

mapper.update:
UPDATE SCM0001M
   SET PLAN_STUS_ID = #{planStusId}
 WHERE PLAN_ID = #{planId}
