mapper.select:
SELECT A.IF_TYPE , B.CODE_NAME AS IF_TYPE_NAME , A.TRAN_STATUS_CD , C.CODE_NAME AS TRAN_STATUS_NAME , A.ERR_CD , D.CODE_NAME AS ERR_NAME , A.ERR_MSG , SUBSTR(A.TRAN_DT, 5, 2) || '-' || SUBSTR(A.TRAN_DT, 7, 2) || '-' || SUBSTR(A.TRAN_DT, 1, 4) AS TRAN_DT , SUBSTR(A.TRAN_TM, 1, 2) || ':' || SUBSTR(A.TRAN_TM, 3, 2) || ':' || SUBSTR(A.TRAN_TM, 5, 4) AS TRAN_TM , SUBSTR(A.RGST_DT, 5, 2) || '-' || SUBSTR(A.RGST_DT, 7, 2) || '-' || SUBSTR(A.RGST_DT, 1, 4) AS RGST_DT , SUBSTR(A.RGST_TM, 1, 2) || ':' || SUBSTR(A.RGST_TM, 3, 2) || ':' || SUBSTR(A.RGST_TM, 5, 4) AS RGST_TM FROM ( SELECT Z.IF_TYPE , Z.TRAN_STATUS_CD , Z.ERR_CD , Z.ERR_MSG , Z.TRAN_DT , Z.TRAN_TM , Z.RGST_DT , Z.RGST_TM FROM ITF0151M Z WHERE Z.TRAN_DT BETWEEN #{tranDtFrom} AND #{tranDtTo}
AND Z.TRAN_STATUS_CD IN
#{item}
UNION ALL SELECT Z.IF_TYPE , Z.TRAN_STATUS_CD , Z.ERR_CD , Z.ERR_MSG , Z.TRAN_DT , Z.TRAN_TM , Z.RGST_DT , Z.RGST_TM FROM ITF0152M Z WHERE Z.TRAN_DT BETWEEN #{tranDtFrom} AND #{tranDtTo}
AND Z.TRAN_STATUS_CD IN
#{item}
UNION ALL SELECT Z.IF_TYPE , Z.TRAN_STATUS_CD , Z.ERR_CD , Z.ERR_MSG , Z.TRAN_DT , Z.TRAN_TM , Z.RGST_DT , Z.RGST_TM FROM ITF0153M Z WHERE Z.TRAN_DT BETWEEN #{tranDtFrom} AND #{tranDtTo}
AND Z.TRAN_STATUS_CD IN
#{item}
UNION ALL SELECT Z.IF_TYPE , Z.TRAN_STATUS_CD , Z.ERR_CD , Z.ERR_MSG , Z.TRAN_DT , Z.TRAN_TM , Z.RGST_DT , Z.RGST_TM FROM ITF0155M Z WHERE Z.TRAN_DT BETWEEN #{tranDtFrom} AND #{tranDtTo}
AND Z.TRAN_STATUS_CD IN
#{item}
UNION ALL SELECT Z.IF_TYPE , Z.TRAN_STATUS_CD , Z.ERR_CD , Z.ERR_MSG , Z.TRAN_DT , Z.TRAN_TM , Z.RGST_DT , Z.RGST_TM FROM ITF0156M Z WHERE Z.TRAN_DT BETWEEN #{tranDtFrom} AND #{tranDtTo}
AND Z.TRAN_STATUS_CD IN
#{item}
) A LEFT OUTER JOIN SYS0013M B ON (A.IF_TYPE = B.CODE AND B.CODE_MASTER_ID = 352) /* 352 : SCM Interface Type */ LEFT OUTER JOIN SYS0013M C ON (A.TRAN_STATUS_CD = C.CODE AND C.CODE_MASTER_ID = 400) /* 400 : SCM Interface Trans Status */ LEFT OUTER JOIN SYS0013M D ON (A.ERR_CD = D.CODE AND D.CODE_MASTER_ID = 401) /* 401 : SCM Interface Interface Error Code */ WHERE 1 = 1
AND A.IF_TYPE IN
#{item}
ORDER BY A.TRAN_DT DESC
